import React from "react";
import PropTypes from "prop-types";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import styled from "styled-components";
import { Avatar, Tabs, Tab, Typography, Box } from "@material-ui/core";
import { theme as customTheme } from "../../Page";
import {
  Container,
  CaptionText,
  SubText,
  Header
} from "../../styledComponents";
import hasLogo from "../../../public/static/HAS.png";

const StyledTabs = withStyles({
  indicator: {
    display: "flex",
    justifyContent: "center",
    backgroundColor: "transparent",
    "& > div": {
      maxWidth: 40,
      width: "100%",
      backgroundColor: "#635ee7"
    }
  }
})(props => <Tabs {...props} TabIndicatorProps={{ children: <div /> }} />);

const StyledTab = withStyles(theme => ({
  root: {
    textTransform: "none",
    color: "#fff",
    fontWeight: theme.typography.fontWeightRegular,
    fontSize: theme.typography.pxToRem(15),
    marginRight: theme.spacing(1),
    "&:focus": {
      opacity: 1
    }
  }
}))(props => <Tab disableRipple {...props} />);

const useStyles = makeStyles(theme => ({
  small: {
    width: theme.spacing(10),
    height: theme.spacing(10),
    borderRadius: 0,
    backgroundColor: "transparent",
    border: 0
  },

  smaller: {
    width: theme.spacing(7),
    height: theme.spacing(7)
  },
  userAvatar: {
    width: theme.spacing(4.5),
    height: theme.spacing(4.5)
  },
  root: {
    flexGrow: 1
  },
  padding: {
    padding: theme.spacing(3)
  }
}));
const HeaderSection = styled(Container)`
  display: flex;
  width: 100%;
  flex-direction: row;
  padding: 30px 110px;
  background-color: white;
`;
const ContentSection = styled(Container)`
  display: flex;

  flex-direction: column;
  width: 100%;
  padding: 0px 110px;
  background-color: white;
`;
const TabSection = styled(Container)`
  display: flex;
  flex: 1;
  /* background-color: red; */
  flex-direction: column;
  width: 100%;
  padding: 0px 110px;
`;
const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <TabSection>
      {value === index && <Container column>{children}</Container>}
    </TabSection>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired
};

function a11yProps(index) {
  return {
    id: `nav-tab-${index}`,
    "aria-controls": `nav-tabpanel-${index}`
  };
}
const SingleHouse = props => {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  return (
    <Container flex={1}>
      <HeaderSection>
        <Container flex={false} color="transparent">
          <Avatar src={hasLogo} className={classes.small} />
        </Container>
        <Container
          flex={1}
          column
          customStyles={{ paddingLeft: 40, paddingTop: 5 }}
          color="transparent"
        >
          <Header customStyles={{ fontWeight: 400, marginBottom: 0 }}>
            Barcelona
          </Header>
          <SubText customStyles={{ color: "#979a9a" }}>
            KGN606 St. KIGALI, GA 30338, RWANDA
          </SubText>
          <Container
            column
            customStyles={{ paddingTop: 7, paddingBottom: 10 }}
            color="transparent"
          >
            <SubText>last updated 20-March-2020</SubText>
          </Container>
          <Container
            row
            customStyles={{ paddingTop: 10, paddingBottom: 10 }}
            color="transparent"
          >
            <Tag>
              <CaptionText>has Tenant</CaptionText>
            </Tag>
          </Container>
        </Container>
      </HeaderSection>
      <ContentSection>
        <StyledTabs value={value} onChange={handleChange}>
          <StyledTab label="Overview" {...a11yProps("overview")} />
          <StyledTab label="Issues" {...a11yProps("issues")} />
          <StyledTab label="Tenants" {...a11yProps("tenants")} />
          <StyledTab label="Settings" {...a11yProps("settings")} />
        </StyledTabs>
      </ContentSection>
      <TabPanel value={value} index={0}>
        Page One
      </TabPanel>
      <TabPanel value={value} index={1}>
        Page Two
      </TabPanel>
      <TabPanel value={value} index={2}>
        Page Three
      </TabPanel>
    </Container>
  );
};

export default SingleHouse;
