import React from "react";
import styled from "styled-components";
import Link from "next/link";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  Text,
  IconButton,
  SubHeader,
  SubText,
  CaptionText,
  NormalText,
} from "../styledComponents";
import userPhoto from "../../public/static/sandy.jpeg";
import { Avatar, Divider, Chip } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import HouseIcon from "@material-ui/icons/Home";
import BagIcon from "@material-ui/icons/MonetizationOn";
import CheckIcon from "@material-ui/icons/Check";
import CreateBuilding from "./houses/create/CreateBuilding";
const useStyles = makeStyles((theme) => ({
  small: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    borderRadius: 0,
  },
}));
const CardWrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  height: 385px;
  min-width: 320px;
  /* border: 1px solid #d5d8dc; */
  position: relative;
  margin-bottom: 20px;
  border-radius: 2px;
  background-color: white;
  cursor: pointer;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;

const Tag = styled(Container)`
  flex: 1;
  align-items: center;
  justify-content: center;
  border-radius: 10px;
  padding: 5px;
  margin-right: 5px;
`;
const CardHeader = styled(Container)`
  direction: flex;
  flex-direction: column;
  width: 100%;
  padding: 15px;
  background-color: #f7f9f9;
`;

const CardContent = styled(Container)`
  direction: flex;
  flex-direction: column;
  flex: 0;
  width: 100%;
  background-color: transparent;
  align-items: center;
  padding: 15px 0px;
`;

const TenantCard = ({ house, tenant }) => {
  const classes = useStyles();
  return (
    <Link
      style={{ flex: 0 }}
      href={{
        pathname: `/Landlrd/houses/[id]`,
        query: { id: house.id },
      }}
      as={`/Landlrd/houses/${house.id}`}
    >
      <a style={{ textDecoration: "none", flex: 0 }}>
        <CardWrapper>
          <CardHeader>
            <Container row center flex={1} color="transparent">
              <Container column middle flex={1} color="transparent">
                <CaptionText>20 days Overdue</CaptionText>
                <CaptionText>Rent for Jan 2016</CaptionText>
              </Container>
              <Container row center right flex={1} color="transparent">
                <Avatar
                  style={{ backgroundColor: "#52BE80", height: 25, width: 25 }}
                >
                  <CheckIcon color="white" style={{ fontSize: 15 }} />
                </Avatar>
              </Container>
            </Container>
            <Container
              row
              center
              color="transparent"
              customStyles={{ marginTop: 10 }}
            >
              <Container
                row
                center
                middle
                flex={1}
                color="white"
                customStyles={{
                  marginRight: 5,
                  border: `0.5px solid #ECF0F1`,
                  padding: "12px 0px",
                  borderRadius: 2,
                }}
              >
                <HouseIcon
                  color="orange"
                  style={{ fontSize: 18, marginRight: 10 }}
                />
                <CaptionText>Unit #380</CaptionText>
              </Container>
              <Container
                row
                center
                middle
                color="white"
                flex={1}
                customStyles={{
                  marginLeft: 5,
                  border: `0.5px solid #ECF0F1`,
                  padding: "12px 0px",
                  borderRadius: 2,
                }}
              >
                <BagIcon
                  color="purple"
                  style={{ fontSize: 18, marginRight: 10 }}
                />
                <CaptionText>$2400/mon</CaptionText>
              </Container>
            </Container>
          </CardHeader>
          <CardContent>dksdksa</CardContent>
        </CardWrapper>
      </a>
    </Link>
  );
};

export default TenantCard;
