import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Link from "next/link";
import {
  Typography,
  Paper,
  InputBase,
  Divider,
  IconButton,
  GridList,
  Avatar,
  Button,
  GridListTile
} from "@material-ui/core";
import { theme as customTheme } from "../../Page";
import CheckIcon from "@material-ui/icons/CheckCircleOutline";
import BedIcon from "@material-ui/icons/SingleBed";
import BathroomIcon from "@material-ui/icons/Bathtub";
import SearchIcon from "@material-ui/icons/Search";
import CustomDrawer from "../Drawer";
import CreateNewHouse from "./create";
import Houses from "../../queryComponents/Houses";
import {
  Container,
  PrimaryButton,
  CommandButton,
  SubText,
  CaptionText,
  NormalText
} from "../../styledComponents";
import DeleteHouse from "./DeleteHouse";
import loadingImg from "../../../public/static/126.gif";
import UpdateHouse from "./UpdateHouse";
import DataTable from "../../DataTable";

const useStyles = makeStyles(theme => ({
  root: {
    display: "flex",
    flex: 1,
    height: 500,
    flexDirection: "column",
    padding: theme.spacing(1)
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    flexDirection: "row",
    marginBottom: theme.spacing(2)
    // padding: theme.spacing(0)
    // ...theme.mixins.toolbar
  },
  searchRoot: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 300
  },
  input: {
    marginLeft: 10,
    flex: 1
  },
  iconButton: {
    padding: 10
  },
  divider: {
    height: 28,
    margin: 4
  },
  dividerLine: {
    width: "100%"
    // marginTop: 3
  },
  gridList: {
    display: "flex",
    flex: 1,
    // width: "100%",
    height: "100%",
    overflowY: "hidden"
  },
  icon: {
    color: "rgba(255, 255, 255, 0.54)"
  },
  houseTileStyle: {
    margin: 5,
    borderRadius: 2,
    padding: 0,
    backgroundColor: "white",
    boxShadow: `0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
    0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108)`,
    "&:hover": {
      // zIndex: 1,
      // backgroundColor: "#0B1B21",
      transform: `scale(1.005) translate(0.2px, 0px)`
    }
  },
  tileWrapper: {
    width: "100%",
    height: "100%"
    // backgroundColor: "pink"
  },
  imageWrapper: {
    // display: "flex",
    // flex: 1,
    // width: 150,
    padding: 2,
    borderRadius: 4,
    transition: `transform 600ms`
    // "&:hover": {
    //   border: "1px solid red"
    // }
  },
  imageStyle: {
    width: "100%",
    height: 90,
    objectFit: "fit"
  }
}));

const Index = props => {
