import React from "react";
import {
  Container,
  Text,
  CommandButton,
  PrimaryButton,
  SearchInput,
  OutlinedButton,
  SubText,
  ActionButton,
  IconButton,
  Header,
  CaptionText,
  Input,
  TextInput,
} from "../../styledComponents";
import styled from "styled-components";
import HouseItem from "../HouseItem";
import CreateHouse from "./create/CreateHouse";
import { Divider } from "@material-ui/core";
import IncreaseIcon from "@material-ui/icons/ArrowDropUp";
import DecreaseIcon from "@material-ui/icons/ArrowDropDown";
const SearchWrapper = styled(Container)`
  display: flex;
  width: 100%;
  flex-direction: row;
  padding: 20px 110px 10px 110px;
  /* background-color: white; */
`;
const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 0px 110px;
`;
const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const SearchBox = styled(Container)`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding-right: 10px;
  background-color: white;
  height: 32px;
  padding: 0px 5px;
  min-width: 150px;
  margin-left: 7px;
  margin-right: 10px;
`;
const ShowButton = styled(Container)`
  display: flex;
  height: 30px;
  flex-direction: row;
  min-width: 60px;

  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const Houses = (props) => {
  return (
    <Container flex={1}>
      <SearchWrapper>
        <Header>Your Houses</Header>
      </SearchWrapper>
      <Wrapper>
        <Container
          space="space-between"
          center
          row
          customStyles={{ paddingTop: 10, paddingBottom: 10 }}
        >
          <Container row center flex={1}>
            <SubText>Showing:</SubText>
            <ShowButton>
              <Container color="transparent" flex={0.6} column middle>
                <SearchInput
                  customStyles={{
                    fontSize: 15,
                    textAlign: "center",
                    height: "100%",
                    alignItems: "center",
                    justifyContent: "center",
                    lineHeight: 1,
                    padding: 0,
                    width: "100%",
                    border: 0,
                  }}
                />
              </Container>
              <Divider orientation="vertical" />
              <Container color="transparent" column flex={0.4}>
                <Container color="transparent" flex={1} middle center>
                  <IncreaseIcon size={20} style={{ margin: 0, padding: 0 }} />
                </Container>
                <Container color="transparent" flex={1} size={20} middle center>
                  <DecreaseIcon style={{ margin: 0, padding: 0 }} />
                </Container>
              </Container>
            </ShowButton>
          </Container>
          <Container row center flex={1} right>
            <SubText>Search:</SubText>
            <SearchBox>
              <SearchInput
                customStyles={{
                  fontSize: 15,
                  height: "100%",
                  lineHeight: 1,
                  padding: 0,
                  width: "100%",
                  border: 0,
                  backgroundColor: "white",
                }}
              />
            </SearchBox>
            <CreateHouse />
          </Container>
        </Container>
        <Container flex={1} row customStyles={{ flexWrap: "wrap" }}>
          {props.houses &&
            props.houses.map((house) => {
              return <HouseItem key={house.id} house={house} {...props} />;
            })}
        </Container>
      </Wrapper>
    </Container>
  );
};

export default Houses;

