import React from "react";
import styled from "styled-components";
import Link from "next/link";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  Text,
  IconButton,
  SubHeader,
  SubText,
  CaptionText,
  NormalText,
  CommandButton,
} from "../styledComponents";
import userPhoto from "../../public/static/sandy.jpeg";
import { Avatar, Divider, Chip } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import HouseIcon from "@material-ui/icons/Home";
import BagIcon from "@material-ui/icons/MonetizationOn";
import CheckIcon from "@material-ui/icons/Check";
import CreateBuilding from "./houses/create/CreateBuilding";
const useStyles = makeStyles((theme) => ({
  small: {
    width: theme.spacing(8),
    height: theme.spacing(8),
    // borderRadius: 0,
    marginBottom: 10,
  },
}));
const CardWrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  height: 270px;
  min-width: 220px;
  /* border: 1px solid #d5d8dc; */

  margin: 0px 5px 30px 5px;
  border-radius: 2px;
  background-color: white;
  cursor: pointer;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;

const Tag = styled(Container)`
  flex: 1;
  align-items: center;
  justify-content: center;
  border-radius: 10px;
  padding: 5px;
  margin-right: 5px;
`;
const CardHeader = styled(Container)`
  direction: flex;
  flex-direction: column;
  width: 100%;
  padding: 0px 15px 10px 15px;
  background-color: #f7f9f9;
`;

const CardContent = styled(Container)`
  direction: flex;
  flex-direction: column;
  flex: 1;
  width: 100%;
  background-color: transparent;
  align-items: center;
  padding: 15px 0px;
  position: relative;
`;
const CardActions = styled(Container)`
  display: flex;
  flex-direction: row;
  width: 100%;
  position: absolute;
  min-height: 40px;
  background-color: transparent;
  bottom: 0;
`;
const TenantCard = ({ tenant }) => {
  const classes = useStyles();
  const { houseId } = tenant;
  return (
    <CardWrapper>
      <CardHeader>
        <Container
          row
          center
          color="transparent"
          customStyles={{ marginTop: 10 }}
        >
          <Container
            row
            center
            flex={0.7}
            color="white"
            customStyles={{
              marginRight: 5,
              border: `0.5px solid #ECF0F1`,
              padding: "10px 0px",
              borderRadius: 2,
            }}
          >
            <HouseIcon
              color="orange"
              style={{ fontSize: 18, marginRight: 7 }}
            />
            <CaptionText> {houseId.house_heading}</CaptionText>
          </Container>
          <Container
            row
            center
            right
            color="transparent"
            flex={0.3}
            customStyles={{
              padding: "10px 0px",
            }}
          >
            {tenant.isActive && (
              <Avatar
                style={{
                  backgroundColor: "#52BE80",
                  height: 25,
                  width: 25,
                }}
              >
                <CheckIcon color="white" style={{ fontSize: 15 }} />
              </Avatar>
            )}
          </Container>
        </Container>
      </CardHeader>
      <CardContent>
        {tenant.profileImage !== null && (
          <Avatar src={tenant.profileImage} className={classes.small} />
        )}
        {tenant.profileImage === null && (
          <Avatar className={classes.small}>
            {tenant.firstName.charAt(0) + tenant.lastName.charAt(0)}
          </Avatar>
        )}

        <SubText customStyles={{ marginBottom: 2 }}>
          {tenant.firstName + " " + tenant.lastName}
        </SubText>
        <CaptionText customStyles={{ color: "blue", marginBottom: 5 }}>
          {tenant.comm_email}
        </CaptionText>
        <CaptionText customStyles={{ marginBottom: 3 }}>
          {tenant.tel_no}
        </CaptionText>
        <CardActions>
          <Container
            flex={1}
            color="transparent"
            customStyles={{
              borderTop: `0.5px solid #ECF0F1`,
              borderRight: `0.25px solid #ECF0F1`,
            }}
          >
            <CommandButton>Message</CommandButton>
          </Container>
          <Link
            href={{
              pathname: `/Landlrd/tenants/[id]`,
              query: { id: tenant.id },
            }}
            as={`/Landlrd/tenants/${tenant.id}`}
          >
            <a
              style={{
                textDecoration: "none",
                display:'flex',

                flex: 1,
                height: "100%",
                backgroundColor: "red",
              }}
            >
              <Container
                flex={1}
                color="transparent"
                row
                center
                middle
                customStyles={{
                  borderTop: `0.5px solid #ECF0F1`,
                  //   borderLeft: `0.25px solid #ECF0F1`,
                }}
              >
                <CommandButton>View Profile</CommandButton>
              </Container>
            </a>
          </Link>
        </CardActions>
      </CardContent>
    </CardWrapper>
  );
};

export default TenantCard;
