import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  Text,
  CaptionText,
  CommandButton,
  PrimaryButton,
  SearchInput,
  OutlinedButton,
  ActionButton,
  IconButton,
  Header,
  SubHeader,
  SubText,
  NormalText,
} from "../../styledComponents";
import styled from "styled-components";
import MoreIcon from "@material-ui/icons/MoreHoriz";
import NotificationIcon from "@material-ui/icons/NotificationsNone";
import userPhoto from "../../../public/static/sandy.jpeg";
import { Avatar } from "@material-ui/core";
import { useQuery } from "@apollo/react-hooks";
import { PAGINATION_QUERY as HOUSE_COUNT_QUERY } from "../houses";

const useStyles = makeStyles((theme) => ({
  small: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    marginRight: 15,
  },
  badgeStyle: {
    backgroundColor: "red",
    width: theme.spacing(2.5),
    height: theme.spacing(2.5),
    position: "absolute",
    top: -5,
    right: -5,
  },
}));

const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  flex: 0.8;
  width: 100%;
  padding-top: 0px;
  padding-left: 110px;
  padding-bottom: 0px 110px;
  padding-right: 0px 110px;
`;
const SideContainer = styled(Container)`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 10px;
  flex: 0.2;
  background-color: white;
`;
const Card = styled(Container)`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  height: 90px;
  min-width: 220px;
  background-color: white;
  border-radius: 3px;
  padding: 15px;
  margin-right: 20px;
  margin-bottom: 20px;
`;
const ListItem = styled("Container")`
  display: flex;
  flex-direction: row;
  padding: 10px;
  background-color: white;
  border-radius: 3px;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 7px;
`;
const TenantList = styled(Container)`
  flex-direction: row;
  background-color: transparent;
  padding: 10px;
  border-bottom: 0.4px solid #eaecee;
`;
const NotifButton = styled(ActionButton)`
  display: flex;
  position: relative;
  background-color: ${({ theme }) => theme.btnPrimary};
  height: 35px;
  width: 40px;
  border-radius: 3px;
`;

const HouseCountCard = () => {
  const { loading, error, data } = useQuery(PAGINATION_QUERY);

  if (data) {
    return (
      <Card>
        <Container row space="space-between" center color="transparent">
          <CaptionText>Number of houses</CaptionText>
          <IconButton>
            <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
          </IconButton>
        </Container>
        <Container color="transparent">
          <SubHeader customStyles={{ color: "#0078D4" }}>
            {data.housesConnection.aggregate.count}
          </SubHeader>
        </Container>
      </Card>
    );
  }
  return (
    <Card>
      <Container row space="space-between" center color="transparent">
        <CaptionText>Number of houses</CaptionText>
        <IconButton>
          <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
        </IconButton>
      </Container>
      <Container color="transparent">
        <SubHeader customStyles={{ color: "#0078D4" }}>0</SubHeader>
      </Container>
    </Card>
  );
};

const TenantCountCard = () => {
  const { loading, error, data } = useQuery(PAGINATION_QUERY);

  if (data) {
    return (
      <Card>
        <Container row space="space-between" center color="transparent">
          <CaptionText>Number of Tenants</CaptionText>
          <IconButton>
            <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
          </IconButton>
        </Container>
        <Container color="transparent">
          <SubHeader customStyles={{ color: "#0078D4" }}>
            {data.housesConnection.aggregate.count}
          </SubHeader>
        </Container>
      </Card>
    );
  }
  return (
    <Card>
      <Container row space="space-between" center color="transparent">
        <CaptionText>Number of Tenants</CaptionText>
        <IconButton>
          <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
        </IconButton>
      </Container>
      <Container color="transparent">
        <SubHeader customStyles={{ color: "#009688" }}>0</SubHeader>
      </Container>
    </Card>
  );
};

const Dashboard = () => {
  const classes = useStyles();

  return (
    <Container flex={1} row>
      <Wrapper>
        <Container row customStyles={{ paddingTop: 40, paddingBottom: 20 }}>
          <SubHeader customStyles={{ marginRight: 10 }}>
            Welcome back,
          </SubHeader>

          <SubHeader customStyles={{ color: "#009688" }}>Rene La</SubHeader>
        </Container>
        <Container
          customStyles={{ paddingTop: 20, paddingBottom: 20 }}
          wrap
          row
        >
          <HouseCountCard />
          <TenantCountCard />
          <Card>
            <Container row space="space-between" center color="transparent">
              <CaptionText>Number of Tickets</CaptionText>
              <IconButton>
                <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
              </IconButton>
            </Container>
            <Container color="transparent">
              <SubHeader customStyles={{ color: "#ED6161" }}>100</SubHeader>
            </Container>
          </Card>
        </Container>
        <Container flex={1} row>
          <Container flex={0.6} column customStyles={{ paddingRight: 20 }}>
            <Text customStyles={{ marginBottom: 20 }}>
              Latest created houses
            </Text>

            <ListItem>
              <Container flex={0.3} row center color="transparent">
                <Avatar src={userPhoto} className={classes.small} />
                <SubText>Barcelona</SubText>
              </Container>

              <SubText>AMZN</SubText>
              <SubText>456</SubText>
              <MoreIcon />
            </ListItem>
            <ListItem>
              <Container flex={0.3} row center color="transparent">
                <Avatar src={userPhoto} className={classes.small} />
                <SubText>Barcelona</SubText>
              </Container>

              <SubText>AMZN</SubText>
              <SubText>456</SubText>
              <MoreIcon />
            </ListItem>
            <ListItem>
              <Container flex={0.3} row center color="transparent">
                <Avatar src={userPhoto} className={classes.small} />
                <SubText>Barcelona</SubText>
              </Container>

              <SubText>AMZN</SubText>
              <SubText>456</SubText>
              <MoreIcon />
            </ListItem>
            <ListItem>
              <Container flex={0.3} row center color="transparent">
                <Avatar src={userPhoto} className={classes.small} />
                <SubText>Barcelona</SubText>
              </Container>

              <SubText>AMZN</SubText>
              <SubText>456</SubText>
              <MoreIcon />
            </ListItem>
          </Container>
          <Container
            flex={0.4}
            column
            color="transparent"
            customStyles={{ paddingRight: 20 }}
          >
            <Text customStyles={{ marginBottom: 20 }}>Recent tenants</Text>
            <Container column color="white" customStyles={{ borderRadius: 3 }}>
              <TenantList>
                <Container flex={0.15} center middle color="transparent">
                  <Avatar src={userPhoto} className={classes.small} />
                </Container>
                <Container column flex={0.65} middle color="transparent">
                  <CaptionText>Rene La</CaptionText>
                  <NormalText>renela@gmail.com</NormalText>
                </Container>
                <Container flex={0.2} center middle color="transparent">
                  <MoreIcon />
                </Container>
              </TenantList>
              <TenantList>
                <Container flex={0.15} center middle color="transparent">
                  <Avatar src={userPhoto} className={classes.small} />
                </Container>
                <Container column flex={0.65} middle color="transparent">
                  <CaptionText>Rene La</CaptionText>
                  <NormalText>renela@gmail.com</NormalText>
                </Container>
                <Container flex={0.2} center middle color="transparent">
                  <MoreIcon />
                </Container>
              </TenantList>
              <TenantList>
                <Container flex={0.15} center middle color="transparent">
                  <Avatar src={userPhoto} className={classes.small} />
                </Container>
                <Container column flex={0.65} middle color="transparent">
                  <CaptionText>Rene La</CaptionText>
                  <NormalText>renela@gmail.com</NormalText>
                </Container>
                <Container flex={0.2} center middle color="transparent">
                  <MoreIcon />
                </Container>
              </TenantList>
              <TenantList>
                <Container flex={0.15} center middle color="transparent">
                  <Avatar src={userPhoto} className={classes.small} />
                </Container>
                <Container column flex={0.65} middle color="transparent">
                  <CaptionText>Rene La</CaptionText>
                  <NormalText>renela@gmail.com</NormalText>
                </Container>
                <Container flex={0.2} center middle color="transparent">
                  <MoreIcon />
                </Container>
              </TenantList>
            </Container>
          </Container>
        </Container>
      </Wrapper>
      <SideContainer>
        <Container row color="transparent">
          <Container flex={0.7} center middle row color="transparent">
            <Text>Recent Activity</Text>
          </Container>
          <Container row flex={0.3} middle center color="transparent">
            <NotifButton>
              <Avatar className={classes.badgeStyle}>
                <NormalText customStyles={{ color: "white" }}>3</NormalText>
              </Avatar>
              <NotificationIcon
                style={{ fontSize: 30, fontWeight: "normal", color: "white" }}
              />
            </NotifButton>
          </Container>
        </Container>
      </SideContainer>
    </Container>
  );
};

export default Dashboard;
