import React from "react";
import styled from "styled-components";
import Link from "next/link";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  Text,
  CommandButton,
  IconButton,
  SubHeader,
  SubText,
  CaptionText,
  NormalText,
} from "../styledComponents";
import userPhoto from "../../public/static/sandy.jpeg";
import { Avatar, Divider, Chip, Popover, Typography } from "@material-ui/core";
import AddIcon from "@material-ui/icons/MoreHoriz";
import BathIcon from "@material-ui/icons/Bathtub";
import BedIcon from "@material-ui/icons/SingleBed";
import CreateBuilding from "./houses/create/CreateBuilding";
const useStyles = makeStyles((theme) => ({
  small: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    borderRadius: 0,
  },
  popover: {
    pointerEvents: "none",
  },
  paper: {
    padding: theme.spacing(1),
  },
}));
const HouseWrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  padding-top: 10px;
  padding-left: 15px;
  padding-right: 15px;
  height: 300px;
  min-width: 250px;
  margin-right: 10px;
  /* border: 1px solid #d5d8dc; */
  position: relative;
  margin-bottom: 20px;
  border-radius: 2px;
  background-color: white;
  cursor: pointer;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;

const Tag = styled(Container)`
  flex: 1;
  align-items: center;
  justify-content: center;
  border-radius: 10px;
  padding: 5px;
  margin-right: 5px;
`;
const CardHeader = styled(Container)`
  direction: flex;
  flex-direction: row;
  width: 100%;
  align-items: center;
  background-color: transparent;
`;

const ImageWrapper = styled(Container)`
  direction: flex;
  flex-direction: row;
  flex: 0;
  width: 100%;
  /* align-items: center; */
  background-color: transparent;
  padding-top: 5px;
  padding-left: 0px;
  padding-right: 0px;
  padding-bottom: 10px;
  ${(props) => props.customStyles}
`;
const PopContainer = styled(Container)`
  display: flex;
  flex: 1;
  border-radius: 1px;
  box-shadow: 0 1.6px 3.6px 0 rgba(0, 0, 0, 0.132),
    0 0.3px 0.9px 0 rgba(0, 0, 0, 0.108);

  ${(props) => props.customStyles}
`;

const PopListItem = styled(CommandButton)`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 8px 10px;
`;

const HouseItem = ({ house }) => {
  const classes = useStyles();
  const { images, countryId, provinceId, districtId } = house;

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);

  return (
    <Link
      style={{ flex: 0 }}
      href={{
        pathname: `/Landlrd/houses/[id]`,
        query: { id: house.id },
      }}
      as={`/Landlrd/houses/${house.id}`}
    >
      <a style={{ textDecoration: "none", flex: 0 }}>
        <HouseWrapper>
          <CardHeader>
            {house.profileImage !== null && (
              <Avatar src={house.profileImage} className={classes.small} />
            )}
            {house.profileImage === null && <div className={classes.small} />}
            <Container
              flex={1}
              column
              color="transparent"
              customStyles={{ paddingLeft: 10, paddingRight: 10 }}
            >
              <SubHeader customStyles={{ fontSize: "18px", fontWeight: "500" }}>
                {house.house_heading}
              </SubHeader>
              <CaptionText customStyles={{ color: "#0078D4" }}>
                {countryId.name + " ," + districtId.name}
              </CaptionText>
            </Container>

            <Container flex={false} color="transparent">
              <IconButton
                customStyles={{
                  border: "1px solid #E1E1E1",
                  height: 33,
                  width: 33,
                }}
                onMouseEnter={handlePopoverOpen}
                // onMouseLeave={handlePopoverClose}
              >
                <AddIcon style={{ fontSize: 18 }} />
              </IconButton>
            </Container>
          </CardHeader>
          {/*   <CaptionText customStyles={{ marginTop: 3 }}>
            Last updated 2 days ago..
              </CaptionText>*/}
          <ImageWrapper>
            {images.length <= 0 && (
              <Container
                center
                middle
                flex={1}
                color="transparent"
                customStyles={{ width: 100, height: 100 }}
              >
                <Avatar
                  style={{
                    width: 70,
                    height: 70,
                    borderRadius: 70 / 2,
                    backgroundColor: "#F2F3F4",
                  }}
                >
                  <SubHeader> {house.house_heading.charAt(0)}</SubHeader>
                </Avatar>
              </Container>
            )}
          </ImageWrapper>
          <ImageWrapper>
            {images.map((img) => {
              return (
                <Container flex={1} key={img.id}>
                  <Avatar
                    src={img.image}
                    style={{ width: "100%", height: 100, borderRadius: 0 }}
                  />
                </Container>
              );
            })}
          </ImageWrapper>

          <Container color="transparent" column>
            <Container
              flex={1}
              row
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <Container
                flex={1}
                column
                middle
                color="transparent"
                customStyles={{ paddingRight: 10 }}
              >
                <Container color="transparent" row center space="space-between">
                  <CaptionText customStyles={{ fontSize: 10 }}>
                    BEDROOMS
                  </CaptionText>
                  <BedIcon style={{ fontSize: 18 }} />
                </Container>

                <SubText
                  customStyles={{
                    fontWeight: "600",
                    color: "#ED6161",
                    fontSize: 16,
                  }}
                >
                  {house.no_bedrooms}
                </SubText>
              </Container>
              <Container flex={1} column middle color="transparent">
                <Container color="transparent" row center space="space-between">
                  <CaptionText customStyles={{ fontSize: 10 }}>
                    BATHROOMS
                  </CaptionText>
                  <BathIcon style={{ fontSize: 15 }} />
                </Container>

                <SubText
                  customStyles={{
                    fontWeight: "600",
                    color: "#00BCCA",
                    fontSize: 16,
                  }}
                >
                  {house.no_bathrooms}
                </SubText>
              </Container>
            </Container>
            <Container flex={1} row color="transparent">
              <Container flex={1} column middle color="transparent">
                <Container color="transparent" row center space="space-between">
                  <CaptionText customStyles={{ fontSize: 10 }}>
                    ADMIN FEE
                  </CaptionText>
                </Container>
                <SubText customStyles={{ fontWeight: "600" }}>$2000</SubText>
              </Container>
              <Container flex={1} column middle color="transparent">
                <Container color="transparent" row center space="space-between">
                  <CaptionText customStyles={{ fontSize: 10 }}>
                    BROKERAGE FEE
                  </CaptionText>
                </Container>

                <SubText customStyles={{ fontWeight: "600" }}>$2000</SubText>
              </Container>
            </Container>
          </Container>
        </HouseWrapper>
        <Popover
          id="mouse-over-popover"
          className={classes.popover}
          classes={{
            paper: classes.paper,
          }}
          open={open}
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "left",
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "left",
          }}
          onClose={handlePopoverClose}
          disableRestoreFocus
        >
          <PopContainer customStyles={wrapperStyles}>
            <PopListItem
              key={item.id}
              type="button"
              onClick={() => handleClick(item)}
            >

            </PopListItem>
          </PopContainer>
        </Popover>
      </a>
    </Link>
  );
};

export default HouseItem;
