import React from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  Text,
  Input,
  TextInput,
  CaptionText,
  PrimaryButton
} from "../../../styledComponents";
import UploadIcon from "@material-ui/icons/CloudUpload";
import HouseIcon from "@material-ui/icons/Home";
import { withStyles, makeStyles, useTheme } from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar
} from "@material-ui/core";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";

const useStyles = makeStyles(theme => ({
  dialogPaper: {
    height: 700,
    width: 525,
    borderRadius: 2,
    position: "relative"
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none"
  },
  dialogActions: {
    padding: 0,
    margin: 0,
    border: "none"
  },
  dialogContentStyle: {
    display: "flex",
    padding: 0,
    border: "none"
  },
  avatarStyle: {
    width: theme.spacing(12),
    height: theme.spacing(10),
    borderRadius: 5,
    backgroundColor: "#F3F2F1"
  },
  inputStyle: {
    border: 0,
    borderRadius: 4,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 60,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF"
    }
  }
}));

const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;
const Form = styled.form`
  display: flex;
  flex-direction: column;
  padding-top: 40px;
  padding-left: 20px;
  padding-bottom: 20px;
  padding-right: 20px;
  /* background-color: red; */
`;

const CreateHouse = () => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [scroll, setScroll] = React.useState("body");
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <ActionBtn onClick={handleClickOpen}>
        <AddIcon style={{ fontSize: 23, marginRight: 5 }} />
        Add New House
      </ActionBtn>
      <Dialog
        fullScreen={fullScreen}
        onClose={handleClose}
        classes={{
          paper: classes.dialogPaper
        }}
        aria-labelledby="customized-dialog-title"
        open={open}
        scroll={scroll}
      >
        <DialogTitle
          id="scroll-dialog-title"
          classes={{
            root: classes.dialogActionStyle
          }}
        >
          <Container
            row
            center
            color="transparent"
            space="space-between"
            customStyles={{
              paddingTop: 15,
              paddingBottom: 7,
              paddingRight: 10,
              paddingLeft: 20
            }}
          >
            <Text>Create New House</Text>
            {/*<CloseBtn onClick={handleClickOpen}>
              <CloseIcon style={{ fontSize: 23 }} />
          </CloseBtn>*/}
          </Container>
        </DialogTitle>
        <DialogContent
          dividers={scroll === "paper"}
          classes={{
            root: classes.dialogContentStyle
          }}
        >
          <Container
            flex={1}
            column
            color="transparent"
            customStyles={{ paddingTop: 20, paddingBottom: 20 }}
          >
            <Container color="transparent" column middle center>
              <Avatar className={classes.avatarStyle}>
                <HouseIcon style={{ fontSize: 22, color: "#1C1D21" }} />
              </Avatar>
            </Container>
            <Form>
              <fieldset
                style={{
                  padding: 0,
                  flexDirection: "column",
                  flex: 1,
                  border: "none"
                }}
              >
                <Container color="transparent" row>
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingRight: 10 }}
                  >
                    <Input
                      label="House Name"
                      placeholder="type here..."
                      type="text"

                      // value={house_heading}
                      // onChange={e => {
                      //   this.setState({ house_heading: e.target.value });
                      // }}
                    />
                  </Container>
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingLeft: 10 }}
                  >
                    <Input
                      label="House Number"
                      placeholder="type here..."
                      type="text"
                      // value={house_no}
                      // onChange={e => {
                      //   this.setState({ house_no: e.target.value });
                      // }}
                      // customStyles={{ width: 200 }}
                    />
                  </Container>
                </Container>
                <Container
                  color="transparent"
                  column
                  flex={false}
                  customStyles={{ width: "100%" }}
                >
                  <CaptionText
                    customStyles={{ marginBottom: 5, color: "#323130" }}
                  >
                    Description
                  </CaptionText>
                  <textarea
                    type="text"
                    placeholder="start typing here..."
                    className={classes.inputStyle}
                    // value={house_description}
                    // onChange={e => {
                    //   this.setState({
                    //     house_description: e.target.value
                    //   });
                    // }}
                  />
                </Container>

                <Container
                  color="transparent"
                  row
                  customStyles={{ paddingTop: 20 }}
                >
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingRight: 10 }}
                  >
                    <Input
                      label="Bedrooms"
                      type="number"
                      placeholder="0"
                      // value={no_bedrooms}
                      // onChange={e => {
                      //   this.setState({ no_bedrooms: e.target.value });
                      // }}
                    />
                  </Container>
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingLeft: 10 }}
                  >
                    <Input
                      label="Bathrooms"
                      type="number"
                      placeholder="0"
                      // value={no_annex}
                      // onChange={e => {
                      //   this.setState({ no_annex: e.target.value });
                      // }}
                    />
                  </Container>
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingLeft: 10 }}
                  >
                    <Input
                      label="Annex"
                      type="number"
                      placeholder="0"

                      // value={no_bathrooms}
                      // onChange={e => {
                      //   this.setState({ no_bathrooms: e.target.value });
                      // }}
                    />
                  </Container>
                </Container>
                <Container
                  color="transparent"
                  row
                  // customStyles={{ paddingTop:0 }}
                >
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingRight: 10 }}
                  >
                    <Input
                      label="Country"
                      type="text"
                      placeholder="select country"
                    />
                  </Container>
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingLeft: 10 }}
                  >
                    <Input
                      label="City/State"
                      type="text"
                      placeholder="enter state/city"
                      // value={no_annex}
                      // onChange={e => {
                      //   this.setState({ no_annex: e.target.value });
                      // }}
                    />
                  </Container>
                  <Container
                    flex={1}
                    color="transparent"
                    customStyles={{ paddingLeft: 10 }}
                  >
                    <Input
                      label="district"
                      type="text"
                      placeholder="choose district or sectors"

                      // value={no_bathrooms}
                      // onChange={e => {
                      //   this.setState({ no_bathrooms: e.target.value });
                      // }}
                    />
                  </Container>
                </Container>
              </fieldset>
            </Form>
          </Container>
        </DialogContent>
        <DialogActions
          classes={{
            root: classes.dialogActionStyle
          }}
        >
          <Container
            flex={1}
            row
            center
            space="space-between"
            customStyles={{ padding: 10 }}
          >
            <CancelBtn onClick={handleClose}>Cancel</CancelBtn>
            <PrimaryButton onClick={handleClose}>Add House</PrimaryButton>
          </Container>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default CreateHouse;
