import React from "react";
import styled from "styled-components";
import Link from "next/link";
import { makeStyles } from "@material-ui/core/styles";
import {
  Drawer,
  IconButton,
  CssBaseline,
  Fade,
  Badge,
  Slide,
  Popper,
  Menu,
  MenuItem
} from "@material-ui/core";
import { theme as customTheme } from "../Page";
import MoreIcon from "@material-ui/icons/MoreHoriz";
import PersonIcon from "@material-ui/icons/Person";
import {
  Container,
  SubText,
  SubHeader,
  Text,
  CaptionText
} from "../styledComponents";
import hasLogo from "../../public/static/HAS.png";
import userPhoto from "../../public/static/sandy.jpeg";
import { Avatar } from "@material-ui/core";
import ActiveLink from "./ActiveLink";

const useStyles = makeStyles(theme => ({
  menuButton: {
    marginRight: 36
  },
  hide: {
    display: "none"
  },

  drawerPaper: {
    paddingLeft: 100,
    paddingRight: 100,
    height: 64
  },
  menuPaper: {
    display: "flex",
    flex: 1,
    flexDirection: "column",
    backgroundColor: "white",
    width: 200,
    borderRadius: 2,
    marginBottom: 5,
    marginTop: 25
  },
  menuItem: {
    height: 50
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "space-between",
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar
  },
  content: {
    backgroundColor: customTheme.bgPrimary
  },
  small: {
    width: theme.spacing(7),
    height: theme.spacing(7)
  },
  userAvatar: {
    width: theme.spacing(4.5),
    height: theme.spacing(4.5)
  }
}));

const WithLayout = Page => props => {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const menuOpen = Boolean(anchorEl);

  const handleClick = event => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  return (
    <Container flex={1} customStyles={{ minHeight: "100vh" }} column>
      <CssBaseline />
      <Drawer
        variant="permanent"
        anchor="top"
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <Container center row color="transparent" customStyles={{ height: 64 }}>
          <Avatar src={hasLogo} className={classes.small} />
          <Container
            row
            flex={1}
            center
            color="transparent"
            customStyles={{ marginLeft: 20 }}
          >
            <ActiveLink label="Dashboard" href={"/Landlrd/dashboard"} />
            <ActiveLink label="Houses" href={"/Landlrd/houses"} />
            <ActiveLink label="Tenants" href={"/Landlrd/tenants"} />
            <ActiveLink label="Tickets" href={"/Landlrd/tickets"} />
            <ActiveLink label="Reports" href={"/Landlrd/reports"} />
          </Container>
          <Container row flex={false} center right middle color="transparent">
            <IconButton onClick={handleClick} style={{ margin: 0, padding: 0 }}>
              <Badge
                color="secondary"
                overlap="circle"
                badgeContent=" "
                variant="dot"
              >
                <Avatar
                  alt="LOGO"
                  src={userPhoto}
                  onMouseover={handleClick}
                  className={classes.userAvatar}
                />
              </Badge>
            </IconButton>
          </Container>
        </Container>
      </Drawer>
      <Container
        flex={1}
        column
        customStyles={{
          paddingTop: 64
        }}
      >
        <Page />
      </Container>
      <Popper
        open={menuOpen}
        anchorEl={anchorEl}
        placement={"bottom-end"}
        transition
      >
        {({ TransitionProps }) => (
          <Fade {...TransitionProps} timeout={350}>
            <Paper>
              <Typography className={classes.typography}>
                The content of the Popper.
              </Typography>
            </Paper>
          </Fade>
        )}
      </Popper>
      <Menu
        id="fade-menu"
        anchorEl={anchorEl}
        keepMounted
        open={menuOpen}
        onClose={handleClose}
        TransitionComponent={Fade}
        classes={{
          paper: classes.menuPaper
        }}
      >
        <Link href="/landlord/welcome" as="/landlord/my-profile">
          <a>
            <MenuItem onClick={handleClose} className={classes.menuItem}>
              <PersonIcon
                style={{ fontSize: 20, color: "#717D7E", marginRight: 10 }}
              />
              <SubText>My Profile</SubText>
            </MenuItem>
          </a>
        </Link>

        <MenuItem onClick={handleClose} className={classes.menuItem}>
          <SubText>Contact support</SubText>
        </MenuItem>
        <MenuItem onClick={handleClose} className={classes.menuItem}>
          <SubText>Help center</SubText>
        </MenuItem>
      </Menu>
    </Container>
  );
};

export default WithLayout;
