import React from "react";
import styled, { css } from "styled-components";
import { SubText, CaptionText } from "./Text";
import Container from "./Container";

const Input = styled.input`
  border-bottom: 1px solid #d7dbdd;
  border-top: none;
  border-left: none;
  border-right: none;
  padding: 10px;
  background-color: #transparent;
  font-size: 12px;
  &:focus {
    border-bottom: 1.5px solid #5b86c6;
    background-color: transparent;
  }
  ${props => props.customStyles};
`;

const TextInput = ({ label, ...other }) => {
  return (
    <Container column customStyles={{ marginBottom: 10 }} color="transparent">
      <CaptionText customStyles={{ marginBottom: 5 }}>{label}</CaptionText>
      <Input {...other} />
    </Container>
  );
};

export default TextInput;
