import React from "react";
import { withStyles, fade } from "@material-ui/core/styles";
import { Mutation } from "react-apollo";
import { GET_USER_HOUSES } from "../../../queryComponents/Houses";
import { TextField, FormControl, Select } from "@material-ui/core";
import gql from "graphql-tag";
import { theme as customTheme } from "../../../Page";
import Countries from "../../../queryComponents/Countries";
import Provinces from "../../../queryComponents/Provinces";
import Districts from "../../../queryComponents/Districts";
// import Sectors from "../../queryComponents/Sectors";
import HouseTypes from "../../../queryComponents/HouseTypes";
import {
  Container,
  FlatButton,
  Input,
  Text,
  CaptionText,
  PrimaryButton
} from "../../../styledComponents";
import loadingImg from "../../../../public/static/126.gif";

const CREATE_HOUSE_MUTATION = gql`
  mutation CREATE_HOUSE_MUTATION(
    $house_heading: String!
    $house_description: String!
    $house_no: String!
    $no_bathrooms: Int
    $no_bedrooms: Int
    $no_annex: Int
    $dinning_room: Int
    $isActive: Boolean
    $has_in_kitchen: Boolean
    $has_balcony: Boolean
    $has_garden: Boolean
    $has_hotwater: Boolean
    $has_carparking: Boolean
    $house_type: ID
    # $parent_houseId: ID
    $countryId: ID
    $provinceId: ID
    $districtId: ID # $sectorId: ID
  ) {
    createHouse(
      house_heading: $house_heading
      house_description: $house_description
      house_no: $house_no
      no_bathrooms: $no_bathrooms
      no_bedrooms: $no_bedrooms
      no_annex: $no_annex
      dinning_room: $dinning_room
      isActive: $isActive
      has_in_kitchen: $has_in_kitchen
      has_balcony: $has_balcony
      has_garden: $has_garden
      has_hotwater: $has_hotwater
      has_carparking: $has_carparking
      house_type: $house_type
      # parent_houseId: $parent_houseId
      countryId: $countryId
      provinceId: $provinceId
      districtId: $districtId # sectorId: $sectorId
    ) {
      id
      house_heading
      house_description
      house_no
      no_annex
      no_bathrooms
      no_bedrooms
      dinning_room
      isActive
      has_in_kitchen
      has_balcony
      has_garden
      has_hotwater
      has_carparking
      house_type {
        id
      }
      # parent_houseId {
      #   id
      # }
      countryId {
        id
        name
      }
      provinceId {
        id
        name
      }
      districtId {
        id
        name
      }
      # sectorId {
      #   id
      #   name
      # }
    }
  }
`;

const styles = theme => ({
  list: {
    width: "100%",
    alignItems: "center"
  },
  formControl: {
    marginTop: theme.spacing(2),
    maxWidth: 120
  },
  inputStyle: {
    border: 0,
    borderRadius: 4,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 100,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF"
    }
  },
  button: {
    margin: theme.spacing(1)
  },
  actionsContainer: {
    marginBottom: theme.spacing(2)
  },
  resetContainer: {
    padding: theme.spacing(3)
  },
  headerStyle: {
    fontWeight: "500",
    fontSize: 14
  },
  checkStyle: {
    fontWeight: "400",
    fontSize: 12
  }
});

class CreateHouse extends React.Component {
  state = {
    createdHouses: [],
    activeStep: 0,
    house_heading: "",
    house_description: "",
    house_no: "",
    no_annex: "",
    no_bathrooms: "",
    no_bedrooms: "",
    dinning_room: "",
    isActive: false,
    has_in_kitchen: false,
    has_balcony: false,
    has_garden: false,
    has_hotwater: false,
    has_carparking: false,
    house_type: "",
    parent_houseId: "",
    countryId: "",
    provinceId: "",
    districtId: "",
    sectorId: "",
    success: false
  };
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleReset = () => {
    this.setState({ activeStep: 0 });
  };

  render() {
    const {
      activeStep,
      house_heading,
      house_description,
      house_no,
      no_annex,
      no_bathrooms,
      no_bedrooms,
      dinning_room,
      isActive,
      has_in_kitchen,
      has_balcony,
      has_garden,
      has_hotwater,
      has_carparking,
      house_type,
      parent_houseId,
      countryId,
      provinceId,
      districtId,
      sectorId,
      createdHouses,
      success
    } = this.state;
    const { classes } = this.props;

    return (
      <Mutation
        mutation={CREATE_HOUSE_MUTATION}
        refetchQueries={[{ query: GET_USER_HOUSES }]}
        variables={{
          house_heading,
          house_description,
          house_no,
          no_bathrooms: parseInt(no_bathrooms),
          no_bedrooms: parseInt(no_bedrooms),
          no_annex: parseInt(no_annex),
          dinning_room: parseInt(dinning_room),
          isActive,
          has_in_kitchen,
          has_balcony,
          has_garden,
          has_hotwater,
          has_carparking,
          house_type,
          // parent_houseId,
          countryId,
          provinceId,
          districtId
          // sectorId
        }}
      >
        {(createHouse, { loading, error }) => {
          return (
            <Container flex={1}>
              <form
                method="post"
                style={{
                  width: "100%",
                  display: "flex",
                  backgroundColor: "red",
                  flexDirection: "column",
                  flex: 1,
                  position: "relative"
                }}
                onSubmit={async e => {
                  e.preventDefault();
                  await createHouse()
                    .then(res => {
                      this.setState({ success: true });
                    })
                    .catch(err => {
                      alert(err);
                    });

                  this.setState({
                    house_heading: "",
                    house_description: "",
                    house_no: "",
                    no_annex: 0,
                    no_bathrooms: 0,
                    no_bedrooms: 0,
                    dinning_room: 0,
                    isActive: false,
                    has_in_kitchen: false,
                    has_balcony: false,
                    has_garden: false,
                    has_hotwater: false,
                    has_carparking: false,
                    house_type: "",
                    parent_houseId: "",
                    countryId: "",
                    provinceId: "",
                    districtId: "",
                    sectorId: ""
                  });
                }}
              >
                <Container flex={false}>
                  <Text>Create and save new House </Text>
                </Container>
              </form>
            </Container>
          );
        }}
      </Mutation>
    );
  }
}

export default withStyles(styles)(CreateHouse);
