import React from "react";
import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import { theme as customTheme } from "../Page";
import { Container, SubText, SubHeader, Text } from "../styledComponents";
import hasLogo from "../../public/static/HAS.png";
import { Avatar } from "@material-ui/core";
import ActiveLink from "./ActiveLink";

const useStyles = makeStyles(theme => ({
  menuButton: {
    marginRight: 36
  },
  hide: {
    display: "none"
  },

  drawerPaper: {
    paddingLeft: 100,
    paddingRight: 100,
    height: 64
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "space-between",
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar
  },
  content: {
    backgroundColor: customTheme.bgPrimary
  },
  small: {
    width: theme.spacing(7),
    height: theme.spacing(7)
  }
}));

const WithLayout = Page => props => {
  const classes = useStyles();

  return (
    <Container flex={1} customStyles={{ minHeight: "100vh" }} column>
      <CssBaseline />
      <Drawer
        variant="permanent"
        anchor="top"
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <Container
          center
          row
          flex={1}
          color="transparent"
          customStyles={{ height: 64 }}
        >
          <Avatar src={hasLogo} className={classes.small} />
          <Container
            row
            flex={1}
            center
            color="red"
            customStyles={{ marginLeft: 20 }}
          >
            <ActiveLink label="Dashboard" href={"/Landlrd/dashboard"} />
            <ActiveLink label="Houses" href={"/Landlrd/houses"} />
            <ActiveLink label="Tenants" href={"/Landlrd/tenants"} />
            <ActiveLink label="Tickets" href={"/Landlrd/tickets"} />
            <ActiveLink label="Reports" href={"/Landlrd/reports"} />
          </Container>
          <Container row flex={1} center right color="green">
          <Container
          flex={0.85}
          row
          color="white"
          depth={4}
          customStyles={{ marginRight: 8, borderRadius: 2 }}
        >
          <Container middle center flex={0.2} color="transparent">
            <Avatar alt="LOGO" src={userPhoto} className={classes.small} />
          </Container>
          <Container flex={0.8} row color="transparent">
            <Container
              middle
              flex={0.8}
              color="transparent"
              customStyles={{ flexWrap: "no-wrap" }}
            >
              <User>
                {payload => {
                  const { data, loading } = payload;
                  if (!loading && data) {
                    return (
                      <div style={{ flexWrap: "no-wrap" }}>
                        {data.me && (
                          <CaptionText
                            customStyles={{ flexWrap: "no-wrap" }}
                          >
                            {data.me.email}
                          </CaptionText>
                        )}
                      </div>
                    );
                  }
                  return <div />;
                }}
              </User>
            </Container>
            <Container middle center flex={0.2} color="transparent">
              <IconButton
                style={{
                  width: "100%",
                  height: "100%",
                  padding: 0,
                  borderRadius: 0
                }}
                aria-controls="fade-menu"
                aria-haspopup="true"
                onClick={handleClick}
              >
                <MoreIcon />
              </IconButton>
            </Container>
          </Container>
        </Container>
          </Container>
        </Container>
      </Drawer>
      <Container
        flex={1}
        column
        customStyles={{
          paddingTop: 64
        }}
      >
        <Page />
      </Container>
    </Container>
  );
};

export default WithLayout;
