import React from "react";
import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
  NormalText,
} from "../styledComponents";
import userPhoto from "../../public/static/sandy.jpeg";
import { Avatar } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";

const useStyles = makeStyles((theme) => ({
  small: {
    width: theme.spacing(6),
    height: theme.spacing(6),
    borderRadius: 3,
  },
}));
const TicketWrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  padding-top: 10px;
  padding-left: 15px;
  padding-right: 15px;
  height: 385px;
  min-width: 320px;
  border: 1px solid #d5d8dc;
  position: relative;
  margin-bottom: 20px;
  border-radius: 2px;
  background-color: white;
  cursor: pointer;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;

const Wrapper = styled(Container)`
  display: flex;
  flex-direction: row;
  border-bottom: 0.5px solid #eaecee;
  position: relative;
  border-radius: 2px;
  padding-top: 10px;
  padding-bottom: 8px;
  background-color: white;
  cursor: pointer;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;
const Tag = styled(Container)`
  flex: 1;
  align-items: center;
  justify-content: center;
  border-radius: 10px;
  padding: 5px;
  margin-right: 5px;
`;
const CardHeader = styled(Container)`
  direction: flex;
  flex-direction: row;
  width: 100%;
  align-items: center;
  background-color: transparent;
`;

const ImageWrapper = styled(Container)`
  direction: flex;
  flex-direction: row;
  flex: 1;
  width: 100%;
  /* align-items: center; */
  background-color: transparent;
  padding: 0px;
  margin-top: 15px;
`;

const TicketItem = ({ list, ticket, ...other }) => {
  const classes = useStyles();
  if (list) {
    return (
      <Wrapper {...other}>
        <Container color="transparent" flex={0.1} row middle center>
          <Avatar src={userPhoto} className={classes.small} />
        </Container>
        <Container color="transparent" flex={0.16} column middle>
          <SubText customStyles={{ fontWeight: "bold", fontSize: "12px" }}>
           {ticket.reported_by.user}
          </SubText>
          <CaptionText>Sat, Mar. 28, 2020</CaptionText>
        </Container>
        <Container color="transparent" flex={0.74} column middle>
          <SubText customStyles={{ fontWeight: "bold", fontSize: "14px" }}>
            {ticket.title}
          </SubText>
          <CaptionText>{ticket.description}</CaptionText>
        </Container>
      </Wrapper>
    );
  }
  return (
    <TicketWrapper {...other}>
      <CardHeader>
        <Avatar src={userPhoto} className={classes.small} />
        <Container
          flex={1}
          column
          color="transparent"
          customStyles={{
            paddingLeft: 10,
            paddingRight: 5,
          }}
        >
          <Text customStyles={{ fontSize: "14px", fontWeight: "500" }}>
            {ticket.title}
          </Text>
          <NormalText customStyles={{ color: "#0078D4" }}>
            Sat, Mar. 28, 2020
          </NormalText>
        </Container>
      </CardHeader>

      <ImageWrapper>
        <Container flex={1} middle center customStyles={{ padding: 40 }}>
          <Avatar
            src={userPhoto}
            style={{ width: "100%", height: "auto", borderRadius: 0 }}
          />
        </Container>
      </ImageWrapper>
    </TicketWrapper>
  );
};

export default TicketItem;
