import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import ListIcon from "@material-ui/icons/List";
import TicketItem from "../TicketItem";
import SingleTicket from "../tickets/SingleTicket";
import HouseTenantsQuery from "../../queryComponents/landlord/HouseTenants";
import TenantCard from "../TenantCard";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 5px;
`;

const Tenants = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };
  return (
    <HouseTenantsQuery id={house.id}>
      {({ data, loading, error }) => {
        if (data) {
          const tenants = data.getHouseTenants;

          return (
            <Container flex={1} customStyles={{ paddingTop: 30 }}>
              <Container row center flex={1} customStyles={{ width: "100%" }}>
                <Avatar
                  style={{
                    backgroundColor: "purple",
                    width: 35,
                    height: 35,
                    alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <ListIcon style={{ fontSize: 23 }} />
                </Avatar>
                <Container
                  column
                  //   middle
                  flex={1}
                  customStyles={{
                    width: "100%",
                    paddingLeft: 15,
                    paddingTop: 3,
                  }}
                >
                  <SubHeader
                    customStyles={{
                      fontSize: 15,
                      marginBottom: 0,
                      fontWeight: "600",
                    }}
                  >
                    Recent ticket on Barcelona
                  </SubHeader>
                </Container>
              </Container>
              <Container
                column
                customStyles={{ paddingTop: 20, paddingBottom: 20 }}
              >
                {tenants.map((tenant) => {
                  return (
                    <TenantCard key={tenant.id} house={tenant} {...other} />
                  );
                })}
              </Container>
            </Container>
          );
        }
        return null;
      }}
    </HouseTenantsQuery>
  );
};

export default Tenants;
