import React from "react";
import styled from "styled-components";
import { fade } from "@material-ui/core/styles";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import PhoneIcon from "@material-ui/icons/LocalPhone";
import TenantIcon from "@material-ui/icons/PersonPin";
import BusinessIcon from "@material-ui/icons/Business";
import ArrowIcon from "@material-ui/icons/ArrowForward";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GardenIcon from "@material-ui/icons/AmpStories";
import CarIcon from "@material-ui/icons/DirectionsCar";
import GraphIcon from "@material-ui/icons/ShowChart";
import ViewIcon from "@material-ui/icons/Style";
import MiniTicketItem from "./MiniTicketItem";
import MbanzaLogo from "../../../public/static/mbanza.png";
import SingleTicket from "../tickets/SingleTicket";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 2px 15px;
  margin: 3px 3px;
`;
const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  flex: 1;
  padding-top: 30px;
  max-height: calc(100vh - 115px);
  overflow: scroll;
  ::-webkit-scrollbar {
    display: none;
  }
`;

const TenantCard = ({ tenant }) => {
  return (
    <Container
      flex={0.5}
      row
      customStyles={{
        borderRadius: 3,
        padding: 20,
        marginRight: 8,
      }}
      color="white"
    >
      <Container
        row
        middle
        flex={0.1}
        color="transparent"
        customStyles={{ height: "100%" }}
      >
        <TenantIcon style={{ fontSize: 20 }} />
      </Container>
      <Container column space={"space-between"} flex={0.8} color="transparent">
        <SubText
          customStyles={{
            fontSize: 16,

            marginBottom: 0,
            marginTop: 0,
          }}
        >
          Tenant
        </SubText>
        <Container
          color="transparent"
          customStyles={{ paddingTop: 10, paddingBottom: 10 }}
        >
          <SubHeader
            customStyles={{
              fontSize: 18,
            }}
          >
            {tenant.firstName + " " + tenant.lastName}
          </SubHeader>
        </Container>
        <Container
          row
          center
          color="transparent"
          customStyles={{ paddingTop: 5, paddingBottom: 5 }}
        >
          <Avatar
            style={{
              backgroundColor: fade("#F7DC6F", 0.3),
              width: 25,
              height: 25,
              marginRight: 8,
            }}
          >
            <MailIcon style={{ fontSize: 15, color: "#F1C40F" }} />
          </Avatar>
          <SubText>{tenant.comm_email}</SubText>
        </Container>
        <Container
          row
          center
          color="transparent"
          customStyles={{ paddingTop: 1, paddingBottom: 1 }}
        >
          <Avatar
            style={{
              backgroundColor: fade("#58D68D", 0.3),
              width: 25,
              height: 25,
              marginRight: 8,
            }}
          >
            <PhoneIcon style={{ fontSize: 15, color: "#27AE60" }} />
          </Avatar>
          <SubText>{tenant.tel_no}</SubText>
        </Container>
      </Container>
      <Container
        row
        middle
        flex={0.1}
        color="transparent"
        customStyles={{ height: "100%", paddingTop: 2 }}
      >
        <ArrowIcon style={{ fontSize: 18 }} />
      </Container>
    </Container>
  );
};

const ManagerCard = ({ managingAgent }) => {
  return (
    <Container
      flex={0.5}
      row
      customStyles={{
        borderRadius: 3,
        padding: 20,
        marginRight: 8,
      }}
      color="white"
    >
      <Container
        row
        middle
        flex={0.1}
        color="transparent"
        customStyles={{ height: "100%" }}
      >
        <BusinessIcon style={{ fontSize: 20 }} />
      </Container>
      <Container column space={"space-between"} flex={0.8} color="transparent">
        <SubText
          customStyles={{
            fontSize: 16,

            marginBottom: 0,
            marginTop: 0,
          }}
        >
          Managing Agent
        </SubText>
        <Container
          color="transparent"
          customStyles={{ paddingTop: 10, paddingBottom: 10 }}
        >
          <SubHeader
            customStyles={{
              fontSize: 18,
            }}
          >
            {managingAgent.company_name}
          </SubHeader>
        </Container>
        <Container
          row
          center
          color="transparent"
          customStyles={{ paddingTop: 5, paddingBottom: 5 }}
        >
          <Avatar
            style={{
              backgroundColor: fade("#F7DC6F", 0.3),
              width: 25,
              height: 25,
              marginRight: 8,
            }}
          >
            <MailIcon style={{ fontSize: 15, color: "#F1C40F" }} />
          </Avatar>
          <SubText>{managingAgent.comm_email}</SubText>
        </Container>
        <Container
          row
          center
          color="transparent"
          customStyles={{ paddingTop: 1, paddingBottom: 1 }}
        >
          <Avatar
            style={{
              backgroundColor: fade("#58D68D", 0.3),
              width: 25,
              height: 25,
              marginRight: 8,
            }}
          >
            <PhoneIcon style={{ fontSize: 15, color: "#27AE60" }} />
          </Avatar>
          <SubText>{managingAgent.tel_no}</SubText>
        </Container>
      </Container>
      <Container
        row
        middle
        flex={0.1}
        color="transparent"
        customStyles={{ height: "100%", paddingTop: 2 }}
      >
        <ArrowIcon style={{ fontSize: 18 }} />
      </Container>
    </Container>
  );
};

const HouseFact = ({ house, house_type }) => {
  return (
    <Container
      flex={1}
      customStyles={{ padding: 10, marginbottom: 20 }}
      row
      center
      color="transparent"
    >
      <Container column flex={1} color="transparent">
        <Container row flex={1} color="transparent">
          <Container column flex={1} color="transparent">
            <Text customStyles={{ fontSize: 15 }}> Type</Text>
          </Container>
          <Container column flex={1} color="transparent">
            <SubText>{house_type.house_type}</SubText>
          </Container>
        </Container>
        <Container row flex={1} color="transparent">
          <Container column flex={1} color="transparent">
            <Text customStyles={{ fontSize: 15 }}> Year Built</Text>
          </Container>
          <Container column flex={1} color="transparent">
            <SubText>{house.year_built}</SubText>
          </Container>
        </Container>
        <Container row flex={1} color="transparent">
          <Container column flex={1} color="transparent">
            <Text customStyles={{ fontSize: 15 }}>Parking</Text>
          </Container>
          <Container column flex={1} color="transparent">
            <SubText>{house.parking + " space(s)"}</SubText>
          </Container>
        </Container>
      </Container>
      <Container column flex={1} color="transparent">
        <Container row flex={1} color="transparent">
          <Container column flex={1} color="transparent">
            <Text customStyles={{ fontSize: 15 }}>Lot</Text>
          </Container>
          <Container column flex={1} color="transparent">
            <SubText>{house.lot + " acres"}</SubText>
          </Container>
        </Container>
      </Container>
    </Container>
  );
};

const Interior = ({ house, appliances }) => {
  return (
    <Container
      flex={1}
      // customStyles={{ paddingTop: 10 }}
      row
      center
      color="transparent"
    >
      <Container column flex={1} color="transparent">
        <Container row flex={1} color="transparent">
          <Container column flex={1} color="transparent">
            <Text customStyles={{ fontSize: 15 }}>Bedrooms</Text>
          </Container>
          <Container column flex={1} color="transparent">
            <SubText>{house.no_bedrooms}</SubText>
          </Container>
        </Container>
        <Container row flex={1} color="transparent">
          <Container column flex={1} color="transparent">
            <Text customStyles={{ fontSize: 15 }}> Bathrooms</Text>
          </Container>
          <Container column flex={1} color="transparent">
            <SubText>{house.no_bathrooms}</SubText>
          </Container>
        </Container>
        <Container row flex={1} color="transparent">
          <Container column flex={1} color="transparent">
            <Text customStyles={{ fontSize: 15 }}>Lot</Text>
          </Container>
          <Container column flex={1} color="transparent">
            <SubText>{house.lot + " sqr ft"}</SubText>
          </Container>
        </Container>
      </Container>
      <Container column flex={1} color="transparent">
        <Container column flex={1} color="transparent">
          <Text customStyles={{ fontSize: 15 }}> Appliances</Text>
        </Container>

        <Container row flex={1} color="transparent" wrap>
          {appliances.map((app, i) => (
            <Tag key={i}>
              <SubText>{app}</SubText>
            </Tag>
          ))}
        </Container>
      </Container>
    </Container>
  );
};
const Overview = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };

  const {
    house_type,
    managingAgent,
    house_status,
    appliances,
    ext_ft,
    utilities,
  } = house;
  return (
    <Wrapper>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <ViewIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            At a glance
          </SubHeader>
        </Container>
      </Container>

      <Container
        row
        flex={1}
        customStyles={{
          marginTop: 10,
          marginBottom: 20,
        }}
        color="transparent"
      >
        <TenantCard
          tenant={{
            firstName: "Rene",
            lastName: "La",
            comm_email: "renela@gmail.com",
            tel_no: "+86 158721827812",
          }}
        />
        <ManagerCard managingAgent={managingAgent} />
      </Container>

      <Container
        column
        flex={1}
        customStyles={{
          borderRadius: 3,
          padding: 15,
          marginTop: 10,
          marginBottom: 20,
        }}
        color="white"
      >
        <Container row center color="transparent">
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Facts and features
          </SubHeader>
        </Container>
        <HouseFact house={house} house_type={house_type} />

        <Container row center color="transparent">
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Interior details
          </SubHeader>
        </Container>
        <Interior house={house} appliances={appliances} />
      </Container>

      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "purple",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <MailIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          //   middle
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Latest tickets
          </SubHeader>
          <CaptionText>View recent ticket on Barcelona</CaptionText>
        </Container>
      </Container>
      <Container column customStyles={{ paddingTop: 20, paddingBottom: 20 }}>
        {[
          {
            id: 1,
            title: "water sink Broken",
            user: "Rene La",
            date: "Sat, Mar. 28, 2020",
            means: "mobile",
            description:
              "Happy Saturday, MailCharts! Time to come back to Netflix...",
          },
          {
            id: 2,
            user: "Sandrine",
            title: "Door handle Broken",
            date: "Mon,Dec. 14, 2019",
            means: "System",
            description: "MailCharts, Ozark Season 3 is now on Netflix",
          },
          {
            id: 3,
            user: "Anto Frero",
            title: "Water tab mulfunction",
            date: "Wed, Jun. 05, 2019",
            means: "mobile",
            description:
              "MailCharts, come back today to more TV shows & movies.",
          },
          {
            id: 4,
            user: "Unix Kevin",
            title: "Door handle Broken",
            date: "Mon,     Dec. 14, 2019",
            means: "System",
            description: "MailCharts, Ozark Season 3 is now on Netflix",
          },
        ].map((issue) => {
          return (
            <MiniTicketItem
              list={true}
              key={issue.id}
              ticket={issue}
              onClick={handleClickOpen}
            />
          );
        })}
      </Container>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <GraphIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          middle
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Latest Activity
          </SubHeader>
        </Container>
      </Container>
      <Container row flex={1} customStyles={{ marginTop: 15 }}>
        <Container
          column
          customStyles={{ height: 300, borderRadius: 5 }}
          flex={0.5}
          color="white"
        />
      </Container>
      <SingleTicket
        ticket={ticket}
        open={openTicket}
        handleClose={handleClose}
      />
    </Wrapper>
  );
};

export default Overview;
