import React, { useState } from "react";
import styled from "styled-components";
import { Mutation } from "react-apollo";
import gql from "graphql-tag";
import { ApolloConsumer } from "react-apollo";
import {
  Container,
  ActionButton,
  Text,
  Input,
  TextInput,
  SubText,
  OutlinedButton,
  SubHeader,
  CaptionText,
  PrimaryButton,
  Dropdown,
} from "../../../styledComponents";
import UploadIcon from "@material-ui/icons/AddPhotoAlternate";
import HouseIcon from "@material-ui/icons/Home";
import { withStyles, makeStyles, useTheme } from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar,
} from "@material-ui/core";
import { GET_HOUSE_TYPES } from "../../../queryComponents/HouseTypes";
import { GET_COUNTRIES } from "../../../queryComponents/Countries";
import { GET_PROVINCES_IN_COUNTRY } from "../../../queryComponents/Provinces";
import { GET_DISTRICTS_IN_PROVINCE } from "../../../queryComponents/Districts";
import { GET_LANDLORD_TENANTS } from "../../../queryComponents/landlord/LandlordTenants";
import { GET_USER_HOUSES } from "../../../queryComponents/landlord/LandlordHouses";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";

const useStyles = makeStyles((theme) => ({
  dialogPaper: {
    height: 700,
    // minWidth: 900,
    borderRadius: 2,
    // position: "relative",
    backgroundColor: "#F2F3F4",
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "#F2F3F4",
  },
  dialogActions: {
    // position: "absolute",
    width: "100%",
    // bottom: 0,
    padding: 0,
    margin: 0,
    border: "none",
    // backgroundColor: "white"
  },
  dialogContentStyle: {
    display: "flex",

    flexDirection: "row",
    marginRight: 10,
    height: "100%",
    backgroundColor: "white",
    border: "none",
    // backgroundColor: "#F2F3F4",
  },
  avatarStyle: {
    position: "relative",
    width: theme.spacing(12),
    height: theme.spacing(10),
    borderRadius: 5,
    backgroundColor: "#F3F2F1",
  },
  inputStyle: {
    border: 0,
    borderRadius: 1,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 60,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF",
    },
  },
}));

const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  position: absolute;
  top: 0;
  right: 0;
  z-index: 1304;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;
const UploadBtn = styled(OutlinedButton)`
  display: flex;
  height: 60px;
  width: 100%;
  border: ${({ theme }) => `1px dashed ${theme.btnPrimary}`};
`;
const CREATE_HOUSE_MUTATION = gql`
  mutation CREATE_HOUSE_MUTATION(
    $house_heading: String!
    $house_no: String!
    $house_description: String!
    $house_type: ID
    $parking: Int
    $no_bedrooms: Int
    $no_bathrooms: Int
    $flooring: String
    $appliances: [Appliances]
    $utilities: [Utilities]
    $ext_ft: [Ext_features]
    $profileImage: String
    $countryId: ID
    $provinceId: ID
    $districtId: ID
  ) {
    createHouse(
      house_heading: $house_heading
      house_no: $house_no
      house_description: $house_description
      house_type: $house_type
      parking: $parking
      no_bedrooms: $no_bedrooms
      no_bathrooms: $no_bathrooms
      flooring: $flooring
      appliances: $appliances
      utilities: $utilities
      ext_ft: $ext_ft
      profileImage: $profileImage
      countryId: $countryId
      provinceId: $provinceId
      districtId: $districtId
    ) {
      id
      house_heading
      house_description
      house_no
    }
  }
`;

const Countries = ({ country, setCountry }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [countryList, setCountryList] = useState([]);
  const [loading, setLoading] = useState(false);

  async function getCountries(event, client) {
    setLoading(true);
    setAnchorEl(event.currentTarget);
    const res = await client.query({ query: GET_COUNTRIES });
    const { countries } = res.data;
    setCountryList(changeObj(countries));
  }

  const changeObj = (arr) => {
    let newArr = [];
    arr.forEach((item, i) => {
      const newItem = {
        id: item.id,
        value: item.name,
      };
      newArr.push(newItem);
    });
    return newArr;
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  return (
    <ApolloConsumer>
      {(client) => (
        <Container
          column
          color="transparent"
          customStyles={{ marginRight: 10 }}
        >
          <Dropdown
            title={country.length > 0 ? country[0].value : "Country"}
            anchorEl={anchorEl}
            handleOpen={(e) => getCountries(e, client)}
            handleClose={handleClose}
            selection={country}
            setSelection={setCountry}
            items={countryList}
            wrapperStyles={{ width: 150 }}
          />
        </Container>
      )}
    </ApolloConsumer>
  );
};

const Provinces = ({ country, province, setProvince }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [provinceList, setProvinceList] = useState([]);
  const [loading, setLoading] = useState(false);

  async function getProvinces(event, client) {
    setLoading(true);
    setAnchorEl(event.currentTarget);
    const res = await client.query({
      query: GET_PROVINCES_IN_COUNTRY,
      variables: { id: country[0].id },
    });

    const provinces = res.data.getProvincesByCountry;
    setProvinceList(changeObj(provinces));
  }
  const changeObj = (arr) => {
    let newArr = [];
    arr.forEach((item, i) => {
      const newItem = {
        id: item.id,
        value: item.name,
      };
      newArr.push(newItem);
    });
    return newArr;
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  return (
    <ApolloConsumer>
      {(client) => (
        <Container
          column
          color="transparent"
          customStyles={{
            pointerEvents: country.length === 0 ? "none" : "auto",
            marginRight: 10,
          }}
        >
          <Dropdown
            title={province.length > 0 ? province[0].value : "Province"}
            anchorEl={anchorEl}
            handleOpen={(e) => getProvinces(e, client)}
            handleClose={handleClose}
            selection={province}
            setSelection={setProvince}
            items={provinceList}
            wrapperStyles={{ width: 150 }}
          />
        </Container>
      )}
    </ApolloConsumer>
  );
};

const Districts = ({ province, district, setDistrict }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [districtList, setDistrictList] = useState([]);
  const [loading, setLoading] = useState(false);

  async function getDistricts(event, client) {
    setLoading(true);
    setAnchorEl(event.currentTarget);
    const res = await client.query({
      query: GET_DISTRICTS_IN_PROVINCE,
      variables: { id: province[0].id },
    });

    const districts = res.data.getDistrictsByProvince;
    setDistrictList(changeObj(districts));
  }
  const changeObj = (arr) => {
    let newArr = [];
    arr.forEach((item, i) => {
      const newItem = {
        id: item.id,
        value: item.name,
      };
      newArr.push(newItem);
    });
    return newArr;
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  return (
    <ApolloConsumer>
      {(client) => (
        <Container
          column
          color="transparent"
          customStyles={{
            pointerEvents: province.length === 0 ? "none" : "auto",
          }}
        >
          <Dropdown
            title={district.length > 0 ? district[0].value : "District"}
            anchorEl={anchorEl}
            handleOpen={(e) => getDistricts(e, client)}
            handleClose={handleClose}
            selection={district}
            setSelection={setDistrict}
            items={districtList}
            wrapperStyles={{ width: 150 }}
          />
        </Container>
      )}
    </ApolloConsumer>
  );
};
const UploadImageContainer = ({
  profileImg,
  setProfile,
  images,
  setImages,
}) => {
  const [img_names, setNames] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const uploadProfile = async (e) => {
    const files = e.target.files;

    setNames([...img_names, files[0].name]);
    const data = new FormData();
    data.append("file", files[0]);
    data.append("upload_preset", "house_assist");

    const res = await fetch(
      "https://api.cloudinary.com/v1_1/dsubmyluz/image/upload/",
      {
        method: "POST",
        body: data,
      }
    );
    const file = await res.json();

    if (file) {
      setProfile(file.secure_url);
      setLoaded(true);
    }
  };

  return (
    <React.Fragment>
      <Container
        flex={0}
        row
        center
        color="transparent"
        customStyles={{ paddingTop: 10, paddingBottom: 5 }}
      >
        <Container
          row
          center
          middle
          flex={0}
          customStyles={{ padding: 3, marginRight: 5 }}
        >
          <UploadIcon style={{ fontSize: 25 }} />
        </Container>
        <Container flex={1} row center color="transparent">
          {img_names.map((img, i) => (
            <CaptionText
              key={i}
              customStyles={{ marginRight: 4, color: "black", fontSize: 13 }}
            >
              {img}
            </CaptionText>
          ))}
        </Container>
      </Container>
      <Container
        flex={1}
        row
        customStyles={{
          pointerEvents: loaded === 0 ? "none" : "auto",
        }}
      >
        <UploadBtn type="button">
          <Input
            accept="image/*"
            id="text-button-file"
            // multiple
            type="file"
            onChange={uploadProfile}
            style={{ marginLeft: 10, width: 200 }}
          />
        </UploadBtn>
      </Container>
    </React.Fragment>
  );
};
const CreateTenant = () => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = useState(false);
  const [scroll, setScroll] = useState("paper");
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const [country, setCountry] = useState([]);
  const [district, setDistrict] = useState([]);
  const [province, setProvince] = useState([]);
  const [profileImage, setProfile] = useState(null);
  const [images, setImages] = useState([]);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  var curr = new Date();
  curr.setDate(curr.getDate() + 3);
  var date = curr.toISOString().substr(0, 10);
  return (
    <Mutation
      mutation={CREATE_HOUSE_MUTATION}
      refetchQueries={[{ query: GET_USER_HOUSES }]}
    >
      {(createHouse, { loading, error }) => (
        <div>
          <ActionBtn onClick={handleClickOpen}>
            <AddIcon style={{ fontSize: 23, marginRight: 5 }} />
            Add Tenant
          </ActionBtn>
          <Dialog
            fullScreen={fullScreen}
            onClose={handleClose}
            classes={{
              paper: classes.dialogPaper,
            }}
            aria-labelledby="customized-dialog-title"
            open={open}
            scroll={scroll}
          >
            <DialogTitle
              id="scroll-dialog-title"
              classes={{
                root: classes.dialogActionStyle,
              }}
            >
              <Container
                row
                center
                color="transparent"
                space="space-between"
                customStyles={{
                  paddingTop: 15,
                  paddingBottom: 7,
                  paddingRight: 10,
                  paddingLeft: 20,
                }}
              >
                <SubHeader> Add Tenant</SubHeader>
                <CancelBtn onClick={handleClose}>Cancel</CancelBtn>
              </Container>
            </DialogTitle>
            <form
              method="post"
              onSubmit={async (e) => {
                e.preventDefault();
                await createHouse()
                  .then((res) => {
                    handleClose();
                  })
                  .catch((err) => {
                    alert(err);
                  });
              }}
              style={{
                display: "flex",
                flexDirection: "row",
                paddingLeft: 20,
                paddingRight: 20,
                paddingTop: 10,
                paddingBottom: 10,
              }}
            >
              <DialogContent
                dividers={scroll === "body"}
                style={{ flex: 1 }}
                classes={{
                  root: classes.dialogContentStyle,
                }}
              >
                <div>
                  <Container row center color="transparent">
                    <CaptionText
                      customStyles={{
                        color: "#323130",
                        fontWeight: "600",
                        marginBottom: 10,
                      }}
                    >
                      Profile Information
                    </CaptionText>
                  </Container>
                  <Container color="transparent" row space="space-between">
                    <Container
                      flex={1}
                      customStyles={{ paddingRight: 10 }}
                      color="transparent"
                    >
                      <Input placeholder="First Name" type="text" />
                    </Container>
                    <Container
                      color="transparent"
                      flex={1}
                      customStyles={{ paddingLeft: 10 }}
                    >
                      <Input placeholder="Last Name" type="text" />
                    </Container>
                  </Container>
                  <Container color="transparent" row space="space-between">
                    <Container
                      flex={1}
                      customStyles={{ paddingRight: 10 }}
                      color="transparent"
                    >
                      <Input placeholder="E-mail" type="text" />
                    </Container>
                    <Container
                      color="transparent"
                      flex={1}
                      customStyles={{ paddingLeft: 10 }}
                    >
                      <Input placeholder="Tel no" type="text" />
                    </Container>
                  </Container>
                  <Container color="transparent" row space="space-between">
                    <Container
                      flex={1}
                      customStyles={{ paddingRight: 10 }}
                      color="transparent"
                    >
                      <Input placeholder="No. of family" type="text" />
                    </Container>
                    <Container
                      color="transparent"
                      flex={1}
                      customStyles={{ paddingLeft: 10 }}
                    >
                      <Input placeholder="Sex" type="text" />
                    </Container>
                  </Container>
                  <Container row center color="transparent">
                    <CaptionText
                      customStyles={{
                        color: "#323130",
                        fontWeight: "600",
                        marginTop: 10,
                        marginBottom: 10,
                      }}
                    >
                      Duration of stay
                    </CaptionText>
                  </Container>
                  <Container row color="transparent">
                    <Input
                      type="date"
                      label="From"
                      defaultValue={date}
                      style={{ marginRight: 20 }}
                    />
                    <Input
                      type="date"
                      label="To.."
                      defaultValue={date}
                      //   style={{ width: 150 }}
                    />
                  </Container>
                  <Container
                    row
                    center
                    color="transparent"
                    customStyles={{ marginBottom: 10 }}
                  >
                    <CaptionText
                      customStyles={{
                        color: "#323130",
                        fontWeight: "600",
                        marginTop: 10,
                      }}
                    >
                      Location
                    </CaptionText>
                  </Container>
                  <Container
                    row
                    color="transparent"
                    customStyles={{ marginBottom: 15 }}
                  >
                    <Countries country={country} setCountry={setCountry} />
                    <Provinces
                      country={country}
                      province={province}
                      setProvince={setProvince}
                    />
                    <Districts
                      district={district}
                      province={province}
                      setDistrict={setDistrict}
                    />
                  </Container>
                  <Container flex={0} row center color="transparent">
                    <CaptionText
                      customStyles={{
                        color: "#323130",
                        fontWeight: "600",
                        marginBottom: 10,
                      }}
                    >
                      Add House Tenant
                    </CaptionText>
                  </Container>

                  <Container
                    flex={0}
                    row
                    space="space-between"
                    color="transparent"
                  >
                    <Container flex={0.3} column color="transparent">
                      <Tenants tenant={tenant} setTenant={setTenant} />
                    </Container>
                  </Container>
                  <Container row center color="transparent">
                    <CaptionText
                      customStyles={{
                        color: "#323130",
                        fontWeight: "600",
                        marginBottom: 10,
                      }}
                    >
                      Profile picture
                    </CaptionText>
                  </Container>
                  <UploadImageContainer
                    profileImg={profileImage}
                    setProfile={setProfile}
                    images={images}
                    setImages={setImages}
                  />
                </div>
              </DialogContent>
            </form>
          </Dialog>
        </div>
      )}
    </Mutation>
  );
};

export default CreateTenant;
