import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  Text,
  CaptionText,
  CommandButton,
  PrimaryButton,
  SearchInput,
  OutlinedButton,
  ActionButton,
  IconButton,
  Header,
  SubHeader,
  SubText,
  NormalText,
} from "../../styledComponents";
import styled from "styled-components";
import User from "../../queryComponents/User";
import MoreIcon from "@material-ui/icons/MoreHoriz";
import NotificationIcon from "@material-ui/icons/NotificationsNone";
import userPhoto from "../../../public/static/sandy.jpeg";
import { Avatar } from "@material-ui/core";
import { useQuery } from "@apollo/react-hooks";
import { PAGINATION_QUERY as HOUSE_COUNT_QUERY } from "../houses";
import { PAGINATION_QUERY as TENANTS_COUNT_QUERY } from "../tenants";
import { PAGINATION_QUERY as TICKETS_COUNT_QUERY } from "../tickets/AllTickets";
import { GET_USER_HOUSES } from "../../queryComponents/landlord/LandlordHouses";
import { GET_LANDLORD_TENANTS } from "../../queryComponents/landlord/LandlordTenants";
import houseIcon from "../../../public/static/web.png";

const useStyles = makeStyles((theme) => ({
  small: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    marginRight: 15,
  },
  badgeStyle: {
    backgroundColor: "red",
    width: theme.spacing(2.5),
    height: theme.spacing(2.5),
    position: "absolute",
    top: -5,
    right: -5,
  },
}));

const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  flex: 0.8;
  width: 100%;
  padding-top: 0px;
  padding-left: 110px;
  padding-bottom: 0px 110px;
  padding-right: 0px 110px;
`;
const SideContainer = styled(Container)`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 10px;
  flex: 0.2;
  background-color: white;
`;
const Card = styled(Container)`
  display: flex;
  flex-direction: row;
  justify-content: center;
  height: 90px;
  min-width: 180px;
  background-color: white;
  border-radius: 3px;
  padding: 15px;
  margin-right: 20px;
  margin-bottom: 20px;
`;
const ListItem = styled("Container")`
  display: flex;
  flex-direction: row;
  padding: 10px;
  background-color: white;
  border-radius: 3px;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 7px;
`;
const TenantList = styled(Container)`
  flex-direction: row;
  background-color: transparent;
  padding: 10px;
  border-bottom: 0.4px solid #eaecee;
`;
const NotifButton = styled(ActionButton)`
  display: flex;
  position: relative;
  background-color: ${({ theme }) => theme.btnPrimary};
  height: 35px;
  width: 40px;
  border-radius: 3px;
`;

const HouseCountCard = () => {
  const { loading, error, data } = useQuery(HOUSE_COUNT_QUERY);

  if (data) {
    return (
      <Card>
        <Container
          row
          space="space-between"
          middle
          center
          color="transparent"
          flex={0.5}
        >
          <img src={houseIcon} style={{ width: 40, height: 40 }} />
        </Container>
        <Container color="transparent" column middle flex={0.5}>
          <SubHeader customStyles={{ color: "#0078D4" }}>
            {data.housesConnection.aggregate.count}
          </SubHeader>
        </Container>
      </Card>
    );
  }
  return (
    <Card>
      <Container row space="space-between" center color="transparent">
        <CaptionText>Number of houses</CaptionText>
        <IconButton>
          <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
        </IconButton>
      </Container>
      <Container color="transparent">
        <SubHeader customStyles={{ color: "#0078D4" }}>0</SubHeader>
      </Container>
    </Card>
  );
};

const TenantCountCard = () => {
  const { loading, error, data } = useQuery(TENANTS_COUNT_QUERY);

  if (data) {
    return (
      <Card>
        <Container row space="space-between" center color="transparent">
          <CaptionText>Number of Tenants</CaptionText>
          <IconButton>
            <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
          </IconButton>
        </Container>
        <Container color="transparent">
          <SubHeader customStyles={{ color: "#0078D4" }}>
            {data.tenantsConnection.aggregate.count}
          </SubHeader>
        </Container>
      </Card>
    );
  }
  return (
    <Card>
      <Container row space="space-between" center color="transparent">
        <CaptionText>Number of Tenants</CaptionText>
        <IconButton>
          <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
        </IconButton>
      </Container>
      <Container color="transparent">
        <SubHeader customStyles={{ color: "#009688" }}>0</SubHeader>
      </Container>
    </Card>
  );
};

const TicketCountCard = () => {
  const { loading, error, data } = useQuery(TICKETS_COUNT_QUERY);

  if (data) {
    return (
      <Card>
        <Container row space="space-between" center color="transparent">
          <CaptionText>Number of Tenants</CaptionText>
          <IconButton>
            <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
          </IconButton>
        </Container>
        <Container color="transparent">
          <SubHeader customStyles={{ color: "#0078D4" }}>
            {data.ticketsConnection.aggregate.count}
          </SubHeader>
        </Container>
      </Card>
    );
  }
  return (
    <Card>
      <Container row space="space-between" center color="transparent">
        <CaptionText>Number of Tenants</CaptionText>
        <IconButton>
          <MoreIcon style={{ fontSize: 25, color: "#979a9a" }} />
        </IconButton>
      </Container>
      <Container color="transparent">
        <SubHeader customStyles={{ color: "#009688" }}>0</SubHeader>
      </Container>
    </Card>
  );
};
const RecentCreatedHouses = () => {
  const { loading, error, data } = useQuery(GET_USER_HOUSES, {
    variables: {
      first: 4,
    },
  });
  const classes = useStyles();

  if (data) {
    const houses = data.getUserHouses;
    return (
      <Container flex={0.6} column customStyles={{ paddingRight: 20 }}>
        <Text customStyles={{ marginBottom: 20 }}>Latest created houses</Text>
        {houses.map((house) => (
          <ListItem key={house.id}>
            <Container flex={0.6} row center color="transparent">
              {house.profileImage === null ? (
                <Avatar className={classes.small}>
                  <SubHeader>{house.house_heading.charAt(0)}</SubHeader>
                </Avatar>
              ) : (
                <Avatar src={house.profileImage} className={classes.small} />
              )}
              <SubText>{house.house_heading}</SubText>
            </Container>

            <SubText>{house.house_no}</SubText>
            <SubText>456</SubText>
            <MoreIcon />
          </ListItem>
        ))}
      </Container>
    );
  }
  return (
    <Container flex={0.6} column customStyles={{ paddingRight: 20 }}>
      <Text customStyles={{ marginBottom: 20 }}>Latest created houses</Text>
    </Container>
  );
};

const RecentCreatedTenants = () => {
  const { loading, error, data } = useQuery(GET_LANDLORD_TENANTS, {
    variables: {
      first: 5,
    },
  });
  const classes = useStyles();

  if (data) {
    const tenants = data.getLandlordTenants;
    return (
      <Container
        flex={0.4}
        column
        color="transparent"
        customStyles={{ paddingRight: 20 }}
      >
        <Text customStyles={{ marginBottom: 20 }}>Recent tenants</Text>
        <Container column color="white" customStyles={{ borderRadius: 3 }}>
          {tenants &&
            tenants.map((ten) => (
              <TenantList key={ten.id}>
                <Container flex={0.15} center middle color="transparent">
                  {ten.profileImage === null ? (
                    <Avatar className={classes.small}>
                      <SubHeader>
                        {ten.firstName.charAt(0) + "" + ten.lastName.charAt(0)}
                      </SubHeader>
                    </Avatar>
                  ) : (
                    <Avatar src={ten.profileImage} className={classes.small} />
                  )}
                </Container>
                <Container column flex={0.65} middle color="transparent">
                  <CaptionText>{ten.firstName + ten.lastName}</CaptionText>
                  <NormalText>{ten.comm_email}</NormalText>
                </Container>
                <Container flex={0.2} center middle color="transparent">
                  <MoreIcon />
                </Container>
              </TenantList>
            ))}
        </Container>
      </Container>
    );
  }
  return (
    <Container
      flex={0.4}
      column
      color="transparent"
      customStyles={{ paddingRight: 20 }}
    >
      <Text customStyles={{ marginBottom: 20 }}>Recent tenants</Text>
      <Container column color="white" customStyles={{ borderRadius: 3 }} />
    </Container>
  );
};
const Dashboard = () => {
  const classes = useStyles();

  return (
    <Container flex={1} row>
      <Wrapper>
        <Container row customStyles={{ paddingTop: 40, paddingBottom: 20 }}>
          <SubHeader customStyles={{ marginRight: 10 }}>
            Welcome back,
          </SubHeader>
          <User>
            {({ data }) => {
              if (data) {
                const { me } = data;
                return (
                  <SubHeader customStyles={{ color: "#009688" }}>
                    {data.me && me.user_name}
                  </SubHeader>
                );
              }
              return <div />;
            }}
          </User>
        </Container>
        <Container
          customStyles={{ paddingTop: 20, paddingBottom: 20 }}
          wrap
          row
        >
          <HouseCountCard />
          <TenantCountCard />
          <TicketCountCard />
        </Container>
        <Container flex={1} row>
          <RecentCreatedHouses />
          <RecentCreatedTenants />
        </Container>
      </Wrapper>
      <SideContainer>
        <Container row color="transparent">
          <Container flex={0.7} center middle row color="transparent">
            <Text>Recent Activity</Text>
          </Container>
          <Container row flex={0.3} middle center color="transparent">
            <NotifButton>
              {/*<Avatar className={classes.badgeStyle}>
                <NormalText customStyles={{ color: "white" }}></NormalText>
          </Avatar>*/}
              <NotificationIcon
                style={{ fontSize: 30, fontWeight: "normal", color: "white" }}
              />
            </NotifButton>
          </Container>
        </Container>
      </SideContainer>
    </Container>
  );
};

export default Dashboard;
