import React from "react";
import { useRouter } from "next/router";
import styled from "styled-components";
import { Container, SubText } from "./styledComponents";
const NavText = styled(SubText)`
  color: ${({ theme, isActive }) =>
    isActive ? theme.primaryDark : theme.grey};
  font-weight: 500;

  &:hover {
    color: ${({ theme }) => theme.primaryDark};
  }
`;

const NavItem = styled(Container)`
  display: flex;
  flex-direction: row;
  align-items: center;
  background-color: ${({ theme, isActive }) =>
    isActive ? theme.bgPrimary : "transparent"};
  marginBottom:5
  padding: 12px;
  cursor: pointer;
  &:hover {
    background-color: ${({ theme }) => theme.bgPrimary};
  }
`;

function ActiveLink({ children, icon, label, href }) {
  const router = useRouter();

  const handleClick = e => {
    e.preventDefault();
    router.push(href);
  };

  return (
    <a href={href} onClick={handleClick}>
      <NavItem isActive={router.pathname}>
        <Container row left center flex={0} color="transparent"></Container>

        <Container
          row
          left
          center
          flex={1}
          color="transparent"
          customStyles={{ marginLeft: 20 }}
        >
          <NavText isActive={router.pathname === href}>{label}</NavText>
        </Container>
      </NavItem>
    </a>
  );
}

export default ActiveLink;
