import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GardenIcon from "@material-ui/icons/AmpStories";
import CarIcon from "@material-ui/icons/DirectionsCar";
import GraphIcon from "@material-ui/icons/ShowChart";
import ViewIcon from "@material-ui/icons/Style";
import MiniTicketItem from "./MiniTicketItem";
import MbanzaLogo from "../../../public/static/mbanza.png";
import SingleTicket from "../tickets/SingleTicket";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 2px 15px;
  margin: 3px 3px;
`;

const Overview = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };

  const {
    house_type,
    managingAgent,
    house_status,
    appliances,
    ext_ft,
    utilities,
  } = house;
  return (
    <Container flex={1} customStyles={{ paddingTop: 30 }}>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <ViewIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            At a glance
          </SubHeader>
        </Container>
      </Container>

      <Container
        column
        flex={1}
        customStyles={{
          borderRadius: 3,
          padding: 15,
          marginTop: 10,
          marginBottom: 20,
        }}
        color="white"
      >
        <Container row flex={1} color="transparent">
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            space="space-between"
            color="transparent"
          >
            <Container row center space="space-between" color="transparent">
              <Text
                customStyles={{
                  fontSize: 14,
                  fontWeight: 600,
                  marginBottom: 10,
                }}
              >
                Tenant Information
              </Text>
            </Container>

            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Name:</SubText>
              <SubText
                customStyles={{
                  color: "#0078D4",
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                Rene La
              </SubText>
            </Container>
            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Email:</SubText>
              <SubText
                customStyles={{
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                renela@gmail.com
              </SubText>
            </Container>
            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Tel Number:</SubText>
              <SubText
                customStyles={{
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                +250780189335
              </SubText>
            </Container>
            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Location:</SubText>
              <SubText
                customStyles={{
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                Rwanda, Kigali - KN89 st.
              </SubText>
            </Container>
          </Container>
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            space="space-between"
            color="transparent"
          >
            <Text
              customStyles={{ fontSize: 14, fontWeight: 600, marginBottom: 10 }}
            >
              Managing Agent Information
            </Text>
            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Company Name:</SubText>
              <SubText
                customStyles={{
                  color: "#0078D4",
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                Mbanza Property Management
              </SubText>
            </Container>
            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Comm_Email:</SubText>
              <SubText
                customStyles={{
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                renela@gmail.com
              </SubText>
            </Container>
            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Tel Number:</SubText>
              <SubText
                customStyles={{
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                +250780189335
              </SubText>
            </Container>
            <Container
              row
              flex={1}
              center
              color="transparent"
              customStyles={{ marginBottom: 7 }}
            >
              <SubText>Location:</SubText>
              <SubText
                customStyles={{
                  fontSize: 16,
                  marginLeft: 15,
                }}
              >
                Rwanda, Kigali - KN89 st.
              </SubText>
            </Container>
          </Container>
        </Container>
      </Container>

      <Container
        column
        flex={1}
        customStyles={{
          borderRadius: 3,
          padding: 15,
          marginTop: 10,
          marginBottom: 20,
        }}
        color="white"
      >
        <Container row center color="transparent">
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Facts and features
          </SubHeader>
        </Container>
        <Container
          flex={1}
          // customStyles={{ paddingTop: 10 }}
          row
          center
          color="transparent"
        >
          <Container column flex={1} color="transparent">
            <Container row flex={1} color="transparent">
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 15 }}> Type</Text>
              </Container>
              <Container column flex={1} color="transparent">
                <SubText>{house_type.house_type}</SubText>
              </Container>
            </Container>
            <Container row flex={1} color="transparent">
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 15 }}> Year Built</Text>
              </Container>
              <Container column flex={1} color="transparent">
                <SubText>{house.year_built}</SubText>
              </Container>
            </Container>
            <Container row flex={1} color="transparent">
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 15 }}>Parking</Text>
              </Container>
              <Container column flex={1} color="transparent">
                <SubText>{house.parking + " space(s)"}</SubText>
              </Container>
            </Container>
          </Container>
          <Container column flex={1} color="transparent">
            <Container row flex={1} color="transparent">
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 15 }}>Lot</Text>
              </Container>
              <Container column flex={1} color="transparent">
                <SubText>{house.lot + " acres"}</SubText>
              </Container>
            </Container>
          </Container>
        </Container>

        <Container row center color="transparent">
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Interior details
          </SubHeader>
        </Container>
        <Container
          flex={1}
          // customStyles={{ paddingTop: 10 }}
          row
          center
          color="transparent"
        >
          <Container column flex={1} color="transparent">
            <Container row flex={1} color="transparent">
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 15 }}>Bedrooms</Text>
              </Container>
              <Container column flex={1} color="transparent">
                <SubText>{house.no_bedrooms}</SubText>
              </Container>
            </Container>
            <Container row flex={1} color="transparent">
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 15 }}> Bathrooms</Text>
              </Container>
              <Container column flex={1} color="transparent">
                <SubText>{house.no_bathrooms}</SubText>
              </Container>
            </Container>
            <Container row flex={1} color="transparent">
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 15 }}>Lot</Text>
              </Container>
              <Container column flex={1} color="transparent">
                <SubText>{house.lot + " sqr ft"}</SubText>
              </Container>
            </Container>
          </Container>
          <Container column flex={1} color="transparent">
            <Container column flex={1} color="transparent">
              <Text customStyles={{ fontSize: 15 }}> Appliances</Text>
            </Container>

            <Container row flex={1} color="transparent" wrap>
              {appliances.map((app, i) => (
                <Tag key={i}>
                  <SubText>{app}</SubText>
                </Tag>
              ))}
            </Container>
          </Container>
        </Container>
      </Container>

      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "purple",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <MailIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          //   middle
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Latest tickets
          </SubHeader>
          <CaptionText>View recent ticket on Barcelona</CaptionText>
        </Container>
      </Container>
      <Container column customStyles={{ paddingTop: 20, paddingBottom: 20 }}>
        {[
          {
            id: 1,
            title: "water sink Broken",
            user: "Rene La",
            date: "Sat, Mar. 28, 2020",
            means: "mobile",
            description:
              "Happy Saturday, MailCharts! Time to come back to Netflix...",
          },
          {
            id: 2,
            user: "Sandrine",
            title: "Door handle Broken",
            date: "Mon,Dec. 14, 2019",
            means: "System",
            description: "MailCharts, Ozark Season 3 is now on Netflix",
          },
          {
            id: 3,
            user: "Anto Frero",
            title: "Water tab mulfunction",
            date: "Wed, Jun. 05, 2019",
            means: "mobile",
            description:
              "MailCharts, come back today to more TV shows & movies.",
          },
          {
            id: 4,
            user: "Unix Kevin",
            title: "Door handle Broken",
            date: "Mon,     Dec. 14, 2019",
            means: "System",
            description: "MailCharts, Ozark Season 3 is now on Netflix",
          },
        ].map((issue) => {
          return (
            <MiniTicketItem
              list={true}
              key={issue.id}
              ticket={issue}
              onClick={handleClickOpen}
            />
          );
        })}
      </Container>
      {/*<Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <GraphIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          middle
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Latest Activity
          </SubHeader>
        </Container>
      </Container>
      <Container row flex={1} customStyles={{ marginTop: 15 }}>
        <Container
          column
          customStyles={{ height: 300, borderRadius: 5 }}
          flex={0.5}
          color="white"
        />
        </Container>*/}
      <SingleTicket
        ticket={ticket}
        open={openTicket}
        handleClose={handleClose}
      />
    </Container>
  );
};

export default Overview;
