import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import ListIcon from "@material-ui/icons/List";
import TicketItem from "../TicketItem";
import SingleTicket from "../tickets/SingleTicket";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 5px;
`;

const Tickets = () => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };
  return (
    <HouseTickets>
      {({ data, loading, error }) => {
        if (data) {
          return (
            <Container flex={1} customStyles={{ paddingTop: 30 }}>
              <Container row center flex={1} customStyles={{ width: "100%" }}>
                <Avatar
                  style={{
                    backgroundColor: "purple",
                    width: 35,
                    height: 35,
                    alignItems: "center",
                    justifyContent: "center",
                  }}
                >
                  <ListIcon style={{ fontSize: 23 }} />
                </Avatar>
                <Container
                  column
                  //   middle
                  flex={1}
                  customStyles={{
                    width: "100%",
                    paddingLeft: 15,
                    paddingTop: 3,
                  }}
                >
                  <SubHeader
                    customStyles={{
                      fontSize: 15,
                      marginBottom: 0,
                      fontWeight: "600",
                    }}
                  >
                    Recent ticket on Barcelona
                  </SubHeader>
                </Container>
              </Container>
              <Container
                column
                customStyles={{ paddingTop: 20, paddingBottom: 20 }}
              >
                {[
                  {
                    id: 1,
                    title: "water sink Broken",
                    user: "Rene La",
                    date: "Sat, Mar. 28, 2020",
                    means: "mobile",
                    description:
                      "Happy Saturday, MailCharts! Time to come back to Netflix...",
                  },
                  {
                    id: 2,
                    user: "Sandrine",
                    title: "Door handle Broken",
                    date: "Mon,Dec. 14, 2019",
                    means: "System",
                    description: "MailCharts, Ozark Season 3 is now on Netflix",
                  },
                  {
                    id: 3,
                    user: "Anto Frero",
                    title: "Water tab mulfunction",
                    date: "Wed, Jun. 05, 2019",
                    means: "mobile",
                    description:
                      "MailCharts, come back today to more TV shows & movies.",
                  },
                  {
                    id: 4,
                    user: "Unix Kevin",
                    title: "Door handle Broken",
                    date: "Mon,     Dec. 14, 2019",
                    means: "System",
                    description: "MailCharts, Ozark Season 3 is now on Netflix",
                  },
                ].map((issue) => {
                  return (
                    <TicketItem
                      list
                      key={issue.id}
                      ticket={issue}
                      onClick={handleClickOpen}
                    />
                  );
                })}
              </Container>
              <SingleTicket
                ticket={ticket}
                open={openTicket}
                handleClose={handleClose}
              />
            </Container>
          );
        }
        return null;
      }}
    </HouseTickets>
  );
};

export default Tickets;
