import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import ListIcon from "@material-ui/icons/List";
import TicketItem from "../TicketItem";
import SingleTicket from "../tickets/SingleTicket";
import HouseTicketsQuery from "../../queryComponents/landlord/HouseTickets";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 5px;
`;

const Tickets = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };
  return (
    <HouseTicketsQuery id={house.id}>
      {({ data, loading, error }) => {
        if (data) {
          const tickets = data.getHouseTickets;
          console.log(tickets);
          return (
            <Container flex={1} customStyles={{ paddingTop: 30 }}>
              <Container row center flex={1} customStyles={{ width: "100%" }}>
                <Container middle center row flex={0.2} color="transparent">
                  <Text customStyles={{ fontSize: 14 }}>
                    {formatDate(ticket.reported_date)}
                  </Text>
                </Container>
                <Container middle center row flex={0.3} color="transparent">
                  <Text customStyles={{ fontSize: 15 }}>{ticket.title}</Text>
                </Container>
                <Container middle center row flex={0.2} />

                <Container middle center row flex={0.2} color="transparent">
                  <Text customStyles={{ fontSize: 15 }}>
                    {ticket.isClosed ? (
                      <Tag
                        customStyles={{ backgroundColor: fade("#A9DFBF", 0.3) }}
                      >
                        <CaptionText customStyles={{ color: "#212F3D" }}>
                          closed
                        </CaptionText>
                      </Tag>
                    ) : (
                      <Tag
                        customStyles={{ backgroundColor: fade("#A9DFBF", 0.3) }}
                      >
                        <CaptionText customStyles={{ color: "#212F3D" }}>
                          open
                        </CaptionText>
                      </Tag>
                    )}
                  </Text>
                </Container>
                <Container middle center row flex={0.1} color="transparent">
                  <MoreIcon />
                </Container>
              </Container>
              <Container
                column
                customStyles={{ paddingTop: 20, paddingBottom: 20 }}
              >
                {tickets.map((issue) => {
                  return (
                    <TicketItem
                      list
                      key={issue.id}
                      ticket={issue}
                      onClick={handleClickOpen}
                    />
                  );
                })}
              </Container>
              <SingleTicket
                ticket={ticket}
                open={openTicket}
                handleClose={handleClose}
              />
            </Container>
          );
        }
        return null;
      }}
    </HouseTicketsQuery>
  );
};

export default Tickets;
