import React from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  IconButton,
  Text,
  Input,
  TextInput,
  NormalText,
  SubText,
  CaptionText,
  PrimaryButton,
  Header,
  SubHeader,
} from "../../styledComponents";
import userPhoto from "../../../public/static/sandy.jpeg";
import AdjustIcon from "@material-ui/icons/Adjust";
import UserIcon from "@material-ui/icons/Face";
import TimeLineIcon from "@material-ui/icons/Timeline";
import HouseIcon from "@material-ui/icons/Kitchen";
import {
  withStyles,
  makeStyles,
  useTheme,
  fade,
} from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar,
  Divider,
} from "@material-ui/core";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";
import Add from "@material-ui/icons/Add";
import TicketItem from "../TicketItem";

const useStyles = makeStyles((theme) => ({
  dialogPaper: {
    height: 700,
    minWidth: "35%",
    borderRadius: 2,
    backgroundColor: "white",
    position: "relative",
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogActions: {
    position: "absolute",
    width: "100%",
    bottom: 0,
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogContentStyle: {
    display: "flex",
    flex: 1,
    height: "100%",
    padding: 0,
    border: "none",
    backgroundColor: "white",
  },
  avatarStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 1,
    backgroundColor: "#F3F2F1",
  },
  buildingImageStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 2,
    marginRight: 20,
    border: "1.5px dashed #F3F2F1",
    backgroundColor: "#F3F2F1",
  },
  inputStyle: {
    border: 0,
    borderRadius: 4,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 60,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF",
    },
  },
}));

const Wrapper = styled(Container)`
  flex: 1;
  flex-direction: column;
  height: 100%;
  padding: 40px 30px 15px 30px;
  background-color: white;
`;
const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  /* position: absolute;
  top: 0;
  right: 0; */
  z-index: 1304;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const AddBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;
const Form = styled.form`
  display: flex;
  flex-direction: row;
  padding: 0px 40px;
  /* background-color: red; */
`;

const DateWrapperStyles = styled(Container)`
  flex-direction: row;
  border-bottom: ${({ theme }) => `4px solid ${theme.btnPrimary}`};
  padding: 5px 0px;
  flex: 0.4;
  align-items: center;
  background-color: transparent;
`;

const DateHeader = (date) => {
  const pad = (n) => {
    return n < 10 ? "0" + n : n;
  };
  const nth = (d) => {
    if (d > 3 && d < 21) return "th";
    switch (d % 10) {
      case 1:
        return "st";
      case 2:
        return "nd";
      case 3:
        return "rd";
      default:
        return "th";
    }
  };
  var monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sept",
    "Oct",
    "Nov",
    "Dec",
  ];
  var currentDate = new Date();
  var date = currentDate.getDate();
  var month = currentDate.getMonth();
  var year = currentDate.getFullYear();
  const newDate = pad(date);
  return (
    <DateWrapperStyles>
      <SubHeader customStyles={{ fontWeight: "500" }}>{newDate}</SubHeader>
      <Text customStyles={{ fontSize: 14, marginRight: 5 }}>
        {nth(newDate)}
      </Text>
      <SubHeader customStyles={{ fontWeight: "500" }}>
        {monthNames[month]}
      </SubHeader>
    </DateWrapperStyles>
  );
};
const SingleTicket = ({ ticket }) => {
  const classes = useStyles();
  const theme = useTheme();
  const [scroll, setScroll] = React.useState("paper");
  const fullScreen = useMediaQuery(theme.breakpoints.down("md"));
  const [hover, setHover] = React.useState(false);

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const handleHover = () => {
    setHover(true);
  };
  const handleHoverOut = () => {
    setHover(false);
  };

  const { houseId, reported_by } = ticket;
  console.log(ticket);
  return (
    <div style={{ flex: 0 }}>
      <TicketItem list ticket={ticket} onClick={handleOpen} />
      <Dialog
        fullScreen={fullScreen}
        onClose={handleClose}
        classes={{
          paper: classes.dialogPaper,
        }}
        open={open}
        scroll={scroll}
      >
        <DialogTitle
          id="scroll-dialog-title"
          classes={{
            root: classes.dialogActionStyle,
          }}
        >
          <Container
            row
            center
            color="transparent"
            space="space-between"
            customStyles={{
              paddingTop: 15,
              // paddingBottom: 7,
              paddingRight: 10,
              paddingLeft: 30,
            }}
          >
            <Container row center flex={1} color="transparent">
              <DateHeader data={ticket.reported_date} />
            </Container>
            <CloseBtn onClick={() => handleClose()}>
              <CloseIcon style={{ fontSize: 23 }} />
            </CloseBtn>
          </Container>
        </DialogTitle>
        <DialogContent
          classes={{
            root: classes.dialogContentStyle,
          }}
        >
          <Wrapper>
            <Container
              flex={0.3}
              column
              color="transparent"
              space="space-between"
              customStyles={{
                padding: 15,
                border: "1px solid #E1E1E1",
                borderRadius: 10,
              }}
            >
              <Container row center flex={0} color="transparent">
                <TimeLineIcon style={{ fontSize: 20, marginRight: 10 }} />
                <NormalText customStyles={{ fontSize: 13 }}>@</NormalText>
                <CaptionText >18:34 PM</CaptionText>
              </Container>
              <Container
                row
                center
                flex={0}
                color="transparent"
                customStyles={{ marginLeft: 30, padding: "5px 0px" }}
              >
                <Text customStyles={{ fontSize: 18 }}>{ticket.title}</Text>
              </Container>
              <Container
                row
                center
                flex={0}
                color="transparent"
                customStyles={{ marginLeft: 30, padding: "2px 0px" }}
              >
                <CaptionText customStyles={{ fontSize: 14 }}>
                  {ticket.description}
                </CaptionText>
              </Container>
              <Container
                row
                center
                flex={0}
                color="transparent"
                customStyles={{ marginLeft: 30, padding: "2px 0px" }}
              >
                <CloseBtn>
                  <Avatar></Avatar>
                </CloseBtn>
              </Container>
            </Container>
          </Wrapper>
        </DialogContent>
      </Dialog>
    </div>
  );
};

