import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GardenIcon from "@material-ui/icons/AmpStories";
import CarIcon from "@material-ui/icons/DirectionsCar";
import GraphIcon from "@material-ui/icons/ShowChart";
import ViewIcon from "@material-ui/icons/Style";
import TicketItem from "../TicketItem";
import SingleTicket from "../tickets/SingleTicket";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 5px;
`;

const AllTickets = ({ tickets, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [list, setList] = React.useState(true);
  const [openTicket, setOpenTicket] = React.useState(false);

  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };
  return (
    <Container
      flex={1}
      customStyles={{ paddingTop: 30, paddingLeft: 30, paddingRight: 30 }}
    >
    <COntainer>
      <Container center row flex={0.2} color="transparent">
        <Text customStyles={{ fontSize: 14 }}>
          {formatDate(ticket.reported_date)}
        </Text>
      </Container>
      <Container center row flex={0.3} color="transparent">
        <Text customStyles={{ fontSize: 15 }}>{ticket.title}</Text>
      </Container>
      <Container center row flex={0.2} color="transparent" />

      <Container center row flex={0.2} color="transparent">
        {ticket.isClosed ? (
          <Tag customStyles={{ backgroundColor: fade("#A9DFBF", 0.3) }}>
            <CaptionText customStyles={{ color: "#212F3D" }}>
              closed
            </CaptionText>
          </Tag>
        ) : (
          <Tag customStyles={{ backgroundColor: fade("#A9DFBF", 0.3) }}>
            <CaptionText customStyles={{ color: "#212F3D" }}>open</CaptionText>
          </Tag>
        )}
      </Container>
      <Container center row flex={0.1} color="transparent">
        <MoreIcon />
      </Container>
    </COntainer>
      <Container
        customStyles={{
          paddingTop: 20,
          flexDirection: !list ? "row" : "column",
        }}
      >
        {[
          {
            id: 1,
            title: "Faucet broken",

            description:
              "GarageBand is the easiest way to create a great-sounding song on your Mac. Add realistic, impeccably produced and performed drum grooves to your song with Drummer.",
            isClosed: false,
            houseId: {
              house_heading: "Akeza",
            },
            reported_by: {
              user_name: "Rene La",
              userType: ["LANDLORD"],
            },
            reported_way: "system",
            reported_date: new Date(),
            assignedDate: new Date(),
            closedDate: new Date(),
            agent_review:
              "Enable Quick Help and get useful hints wherever you hover your cursor",
            createdAt: new Date(),
            updatedAt: new Date(),
          },
        ].map((issue) => {
          return (
            <SingleTicket
              list={list}
              key={issue.id}
              ticket={issue}
              handleClose={handleClose}
              handleOpen={handleClickOpen}
            />
          );
        })}
      </Container>
    </Container>
  );
};

export default AllTickets;
