import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import styled, { keyframes, css } from "styled-components";
import onClickOutside from "react-onclickoutside";
import Container from "./Container";
import { CaptionText } from "./Text";
import DownIcon from "@material-ui/icons/KeyboardArrowDown";
import { Button, Typography, Popover } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  typography: {
    padding: theme.spacing(2),
  },
}));

const DropWrapper = styled(Container)`
  display: flex;
  /* padding: 15px 10px; */
  background-color: "transparent";
  ${(props) => props.wrapperStyles};
`;

const DropHeader = styled(Container)`
  display: flex;
  flex-direction: row;
  flex: 0;
  align-items: center;
  justify-content: space-between;
  padding: 8px 8px;
  border: 1px solid #d7dbdd;
  border-radius: 4px;
  background-color: #f5f8fa;
  outline: transparent;
  ${(open) =>
    open &&
    css` border: 1px solid #5B86C6,
    background-color: #FEFEFF`}

  ${(props) => props.headerStyles};
`;
const DropHeaderTitle = styled(Container)`
  display: flex;
  flex: 1;
  flex-direction: row;
  align-items: center;
`;

const Dropdown = ({
  title,
  items,
  wrapperStyles,
  headerStyles,
  multiSelect = false,
}) => {
  const classes = useStyles();

  // const [open, setOpen] = useState(false);
  const [selection, setSelection] = useState([]);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const id = open ? "simple-popover" : undefined;

  Dropdown.handleClickOutside = () => {
    // setOpen(false);
    setAnchorEl(null);
  };
  const handleClickOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleClick = (item) => {
    if (!selection.some((current) => current.id === item.id)) {
      if (!multiSelect) {
        setSelection([item]);
      } else if (multiSelect) {
        setSelection([...selection, item]);
      }
    } else {
      let selectionAfterRemoval = selection;
      selectionAfterRemoval = selectionAfterRemoval.filter(
        (current) => current.id !== item.id
      );
      setSelection([...selectionAfterRemoval]);
    }
  };
  const isItemSelected = (item) => {
    if (selection.some((current) => current.id === item.id)) {
      return true;
    }
    return false;
  };
  const toggle = () => {
    setOpen(!open);
  };

  return (
    <DropWrapper>
      <DropHeader
        tabIndex={0}
        role="button"
        onKeyPress={() => handleClickOpen}
        onClick={() => handleClickOpen}
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
        }}
      >
        <DropHeaderTitle open={open}>
          <CaptionText customStyles={{ fontSize: 14, color: "#1c2833" }}>
            {title}
          </CaptionText>
        </DropHeaderTitle>
        <Container column center middle>
          <DownIcon />
        </Container>
      </DropHeader>
      <Popover
        id={id}
        open={open}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: "bottom",
          horizontal: "center",
        }}
        transformOrigin={{
          vertical: "top",
          horizontal: "center",
        }}
      >
        <ul className="dd-list">
          {items.map((item) => {
            return (
              <li className="dd-list-item" key={item.id}>
                <button type="button" onClick={() => handleClick(item)}>
                  <span>{item.value}</span>
                  <span>{isItemSelected(item) && "selected"} </span>
                </button>
              </li>
            );
          })}
        </ul>
      </Popover>
    </DropWrapper>
  );
};
const clickOutsideConfig = {
  handleClickOutside: () => Dropdown.handleClickOutside,
};
// export { DropDown, DropDownItem, SearchStyles };
export default onClickOutside(Dropdown, clickOutsideConfig);
