import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { SubText, CaptionText } from "./Text";
import Container from "./Container";

const useStyles = makeStyles(theme => ({
  inputStyle: {
    border: "1px solid #D7DBDD",
    borderRadius: 4,
    padding: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    fontSize: 12,
    "&:focus": {
      border: "1.5px solid #5B86C6",
      backgroundColor: "#FEFEFF"
    }
  }
}));

const Input = ({ label, customInputStyles, ...other }) => {
  const classes = useStyles();
  return (
    <Container column customStyles={{ marginBottom: 20 }} color="transparent">
      <CaptionText customStyles={{ marginBottom: 5 }}>{label}</CaptionText>
      <input className={classes.inputStyle} {...other} />
    </Container>
  );
};

export default Input;
