import React from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  IconButton,
  Text,
  Input,
  TextInput,
  SubText,
  CaptionText,
  PrimaryButton,
  Header,
  SubHeader,
} from "../../../styledComponents";
import userPhoto from "../../../../public/static/sandy.jpeg";
import CheckIcon from "@material-ui/icons/Done";
import HouseIcon from "@material-ui/icons/Home";
import { withStyles, makeStyles, useTheme } from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar,
  Divider,
} from "@material-ui/core";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";
import Add from "@material-ui/icons/Add";

const useStyles = makeStyles((theme) => ({
  dialogPaper: {
    height: 700,
    width: 525,
    borderRadius: 2,
    backgroundColor: "white",
    position: "relative",
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogActions: {
    position: "absolute",
    width: "100%",
    bottom: 0,
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogContentStyle: {
    display: "flex",
    padding: 0,
    border: "none",
    backgroundColor: "white",
  },
  avatarStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 1,
    backgroundColor: "#F3F2F1",
  },
  buildingImageStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 2,
    marginRight: 20,
    border: "1.5px dashed #F3F2F1",
    backgroundColor: "#F3F2F1",
  },
  inputStyle: {
    border: 0,
    borderRadius: 4,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 60,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF",
    },
  },
}));

const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  /* position: absolute;
  top: 0;
  right: 0; */
  z-index: 1304;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const AddBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;
const Form = styled.form`
  display: flex;
  flex-direction: row;
  padding: 0px 40px;
  /* background-color: red; */
`;

const BuildingsContainer = ({ buildings, setBuildings }) => {
  const classes = useStyles();
  return (
    <Container
      column
      customStyles={{ padding: "10px 40px" }}
      color="transparent"
    >
      <Container flex={false} column color="transparent">
        <Container
          color="transparent"
          row
          center
          flex={1}
          customStyles={{
            paddingTop: 5,
            paddingBottom: 15,
            paddingLeft: 0,
            paddingRight: 5,
          }}
        >
          <Container row flex={1} center color="transparent">
            <Avatar src={userPhoto} className={classes.buildingImageStyle} />
            <Container column flex={1} color="transparent">
              <SubHeader
                customStyles={{
                  fontSize: 12,
                  fontWeight: "600",
                  marginBottom: 0,
                }}
              >
                QINGCHENG VILLAS
              </SubHeader>
              <CaptionText>2 houses</CaptionText>
            </Container>
          </Container>

          <AddBtn>
            {/*hover ? (
              <CheckIcon style={{ fontSize: 18, color: "green" }} />
            ) : (
              <Add style={{ fontSize: 18 }} />
            )*/}
          </AddBtn>
        </Container>
        <Divider style={{ height: "1px" }} />
      </Container>
      <Container flex={false} column color="transparent">
        <Container
          color="transparent"
          row
          center
          flex={1}
          customStyles={{
            paddingTop: 5,
            paddingBottom: 15,
            paddingLeft: 0,
            paddingRight: 5,
          }}
        >
          <Container row flex={1} center color="transparent">
            <Avatar src={userPhoto} className={classes.buildingImageStyle} />
            <Container column flex={1} color="transparent">
              <SubHeader
                customStyles={{
                  fontSize: 12,
                  fontWeight: "600",
                  marginBottom: 0,
                }}
              >
                QINGCHENG VILLAS
              </SubHeader>
              <CaptionText>2 houses</CaptionText>
            </Container>
          </Container>

          <AddBtn>
            {/*hover ? (
              <CheckIcon style={{ fontSize: 18, color: "green" }} />
            ) : (
              <Add style={{ fontSize: 18 }} />
            )*/}
          </AddBtn>
        </Container>
        <Divider style={{ height: "1px" }} />
      </Container>
    </Container>
  );
};

const CreateBuilding = ({ house }) => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [scroll, setScroll] = React.useState("paper");
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const [hover, setHover] = React.useState(false);
  const [building, setBuilding] = React.useState(null);
  const handleHover = () => {
    setHover(true);
  };
  const handleHoverOut = () => {
    setHover(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };
