  border-bottom: 1px solid #d7dbdd;
  border-top: none;
  border-left: none;
  border-right: none;
  padding: 10px;
  background-color: transparent;
  font-size: 12px;
  &:focus {
    border-bottom: 1.6px solid #5b86c6;
    /* background-color: #e5e7e9; */
  }
  ${props => props.customStyles};
`;

const TextInput = ({ label, row, ...other }) => {
