    <Container
      customStyles={{ marginBottom: 10, flexDirection: row ? "row" : "column",
      jus
     }}
      color="transparent"
    >
      <CaptionText customStyles={{ marginBottom: 5 }}>{label}</CaptionText>
      <Input {...other} />
    </Container>
  );
};

export default TextInput;
