const CREATE_HOUSE_MUTATION = gql`
  mutation CREATE_HOUSE_MUTATION(
    $house_heading: String!
    $house_description: String!
    $house_no: String!
    $no_bathrooms: Int
    $no_bedrooms: Int
    $no_annex: Int
    $dinning_room: Int
    $isActive: Boolean
    $has_in_kitchen: Boolean
    $has_balcony: Boolean
    $has_garden: Boolean
    $has_hotwater: Boolean
    $has_carparking: Boolean
    $house_type: ID
    # $parent_houseId: ID
    $countryId: ID
    $provinceId: ID
    $districtId: ID # $sectorId: ID
  ) {
    createHouse(
      house_heading: $house_heading
      house_description: $house_description
      house_no: $house_no
      no_bathrooms: $no_bathrooms
      no_bedrooms: $no_bedrooms
      no_annex: $no_annex
      dinning_room: $dinning_room
      isActive: $isActive
      has_in_kitchen: $has_in_kitchen
      has_balcony: $has_balcony
      has_garden: $has_garden
      has_hotwater: $has_hotwater
      has_carparking: $has_carparking
      house_type: $house_type
      # parent_houseId: $parent_houseId
      countryId: $countryId
      provinceId: $provinceId
      districtId: $districtId # sectorId: $sectorId
    ) {
      id
      house_heading
      house_description
      house_no
      no_annex
      no_bathrooms
      no_bedrooms
      dinning_room
      isActive
      has_in_kitchen
      has_balcony
      has_garden
      has_hotwater
      has_carparking
      house_type {
        id
      }
      # parent_houseId {
      #   id
      # }
      countryId {
        id
        name
      }
      provinceId {
        id
        name
      }
      districtId {
        id
        name
      }
      # sectorId {
      #   id
      #   name
      # }
    }
  }
`;

const HouseTypesList = ({ house_type, setHouseType }) => {
  // const [selection, setSelection] = useState([]);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [options, setOptions] = useState([]);
  const [loading, setLoading] = useState(false);

  async function handleClickOpen(event, client) {
    setLoading(true);
    setAnchorEl(event.currentTarget);
    const res = await client.query({ query: GET_HOUSE_TYPES });
    const { houseTypes } = res.data;
    setOptions(changeObj(houseTypes));
  }
  const changeObj = (arr) => {
    let newArr = [];
    arr.forEach((item, i) => {
      const newItem = {
        id: item.id,
        value: item.house_type,
      };
      newArr.push(newItem);
    });
    return newArr;
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <ApolloConsumer>
      {(client) => (
        <Dropdown
          title={house_type.length > 0 ? house_type[0].value : "choose"}
          anchorEl={anchorEl}
          handleOpen={(e) => handleClickOpen(e, client)}
          handleClose={handleClose}
          selection={house_type}
          setSelection={setHouseType}
          items={options}
          wrapperStyles={{ width: 150 }}
        />
      )}
    </ApolloConsumer>
  );
};

const ApplianceList = ({ list, setList }) => {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [options, setOptions] = useState([
    { id: 1, value: "ADMIN" },
    { id: 2, value: "USER" },
    { id: 3, value: "ITEMCREATE" },
    { id: 4, value: "ITEMUPDATE" },
    { id: 5, value: "ITEMDELETE" },
    { id: 6, value: "PERMISSIONUPDATE" },
  ]);

  const handleClickOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };
  const valuesList = () => {
    return;
    list.map((lst) => <span key={lst.id}>{lst.value}</span>);
  };
  return (
    <Dropdown
      title={list.length > 0}
      anchorEl={anchorEl}
      handleOpen={handleClickOpen}
      multiSelect
      handleClose={handleClose}
      selection={list}
      setSelection={setList}
      items={options}
      wrapperStyles={{ width: 200 }}
    />
  );
};

