              </Container>
              <Container row color="transparent">
                <Container
                  color="transparent"
                  customStyles={{ marginRight: 15 }}
                >
                  <TextInput
                    label="no. bedrooms"
                    placeholder="0"
                    type="number"
                    customStyles={{ textAlign: "center", width: 50 }}
                    row
                    // value={house_heading}
                    // onChange={e => {
                    //   this.setState({ house_heading: e.target.value });
