import React, { useState } from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  IconButton,
  Text,
  Input,
  TextInput,
  SubText,
  CaptionText,
  PrimaryButton,
  Header,
  SubHeader,
} from "../../../styledComponents";
import gql from "graphql-tag";
import { ApolloConsumer, Mutation } from "react-apollo";
import { useMutation } from "@apollo/react-hooks";
import userPhoto from "../../../../public/static/sandy.jpeg";
import CheckIcon from "@material-ui/icons/Done";
import HouseIcon from "@material-ui/icons/Home";
import { withStyles, makeStyles, useTheme } from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar,
  Divider,
} from "@material-ui/core";
import ParentHouses, {
  GET_USER_PARENT_HOUSES,
} from "../../../queryComponents/ParentHouses";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";
import Add from "@material-ui/icons/Add";
import loadingImg from "../../../../public/static/126.gif";

const useStyles = makeStyles((theme) => ({
  dialogPaper: {
    height: 700,
    width: 525,
    borderRadius: 2,
    backgroundColor: "white",
    position: "relative",
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogActions: {
    position: "absolute",
    width: "100%",
    bottom: 0,
    padding: 10,
    margin: 0,
    border: "none",
    // backgroundColor: "red",
  },
  dialogContentStyle: {
    display: "flex",
    padding: 0,
    border: "none",
    backgroundColor: "white",
  },
  avatarStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 1,
    backgroundColor: "#F3F2F1",
  },
  buildingImageStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 2,
    marginRight: 20,
    border: "1.5px dashed #F3F2F1",
    backgroundColor: "#F3F2F1",
