import React from "react";
import { useRouter } from "next/router";
import styled from "styled-components";

const NavText = styled(SubText)`
  color: ${({ theme }) => theme.grey};
  font-weight: 500;
  &:hover {
    color: ${({ theme }) => theme.primaryDark};
  }
`;

const NavItem = styled(Container)`
  display: flex;
  flex-direction: row;
  align-items: center;
  /* border-radius: 100px; */
  padding: 12px;
  cursor: pointer;
  &:hover {
    background-color: ${({ theme }) => theme.bgPrimary};
  }
`;

function ActiveLink({ children, icon, label, href }) {
  const router = useRouter();
  const style = {
    marginRight: 10,
    color: router.pathname === href ? "red" : "black"
  };

  const handleClick = e => {
    e.preventDefault();
    router.push(href);
  };

  return (
    <a href={href} onClick={handleClick} style={style}>
      <Container>
        <Container row left center flex={0} color="transparent">
          <DashboardIcon style={{ fontSize: 20, color: "#717D7E" }} />
        </Container>

        <Container
          row
          left
          center
          flex={1}
          color="transparent"
          customStyles={{ marginLeft: 20 }}
        >
          <NavText>Dashboard</NavText>
        </Container>
      </Container>
      {children}
    </a>
  );
}

export default ActiveLink;
