import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GraphIcon from "@material-ui/icons/ShowChart";
import MiniTicketItem from "./MiniTicketItem";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;

const Overview = () => {
  return (
    <Container flex={1} customStyles={{ paddingTop: 30 }}>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <GraphIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            At a glance
          </SubHeader>
          <CaptionText>
            Quick summary about house Barcelona to help you with your email
            reporting.
          </CaptionText>
        </Container>
      </Container>

      <Container
        column
        flex={1}
        customStyles={{
          borderRadius: 3,
          padding: 15,
          marginTop: 10,
          marginBottom: 20
        }}
        color="white"
      >
        <Container row flex={1} color="transparent">
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            space="space-between"
            color="transparent"
          >
            <Text
              customStyles={{ fontSize: 14, fontWeight: 600, marginBottom: 10 }}
            >
              CURRENT TENANT
            </Text>
            <Container row flex={1} center color="transparent">
              <Avatar
                style={{
                  border: "1px dashed #0078D4",
                  backgroundColor: "transparent",
                  width: 30,
                  height: 30,
                  borderRadius: 3,
                  marginRight: 20
                }}
              />
              <SubHeader customStyles={{ color: "#0078D4", fontSize: 16 }}>
                Rene La
              </SubHeader>
            </Container>
          </Container>
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            space="space-between"
            color="transparent"
          >
            <Text
              customStyles={{ fontSize: 14, fontWeight: 600, marginBottom: 10 }}
            >
              MANAGEMENT AGENT
            </Text>
            <Container row flex={1} center color="transparent">
              <Avatar
                style={{
                  border: "1px dashed #0078D4",
                  backgroundColor: "transparent",
                  width: 30,
                  height: 30,
                  borderRadius: 3,
                  marginRight: 20
                }}
              />
              <SubHeader customStyles={{ color: "#0078D4", fontSize: 16 }}>
                Mbanza Property management Agency
              </SubHeader>
            </Container>
          </Container>
        </Container>

        <Divider />
        <Container
          column
          flex={1}
          color="transparent"
          customStyles={{
            paddingTop: 20,
            paddingBottom: 15,
            paddingLeft: 15,
            paddingRight: 15
          }}
        >
          <Text customStyles={{ fontSize: 14, fontWeight: 600 }}>
            HOUSE FEATURES
          </Text>
        </Container>
        <Container row flex={1} color="transparent">
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            space="space-between"
            color="transparent"
          >
            <Container row flex={1} center color="transparent">
              <Avatar
                style={{
                  // border: "1px dashed #0078D4",
                  backgroundColor: "#14a395",
                  width: 30,
                  height: 30,
                  borderRadius: 3,
                  marginRight: 20
                }}
              >
                <BathroomIcon style={{ fontSize: 23 }} />
              </Avatar>
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 14, marginBottom: 0 }}>
                  Bathrooms
                </Text>
                <SubHeader
                  customStyles={{
                    color: "#0078D4",
                    fontSize: 16,
                    marginTop: 0
                  }}
                >
                  3
                </SubHeader>
              </Container>
            </Container>
          </Container>
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            middle
            color="transparent"
          >
            <Container row flex={1} center color="transparent">
              <Avatar
                style={{
                  // border: "1px dashed #0078D4",
                  backgroundColor: "#19c4d0",
                  width: 30,
                  height: 30,
                  borderRadius: 3,
                  marginRight: 20
                }}
              >
                <BedIcon style={{ fontSize: 23 }} />
              </Avatar>
              <Container column flex={1} color="transparent">
                <Text customStyles={{ fontSize: 14, marginBottom: 0 }}>
                  Bedrooms
                </Text>
                <SubHeader
                  customStyles={{
                    color: "#0078D4",
                    fontSize: 16,
                    marginTop: 0
                  }}
                >
                  3
                </SubHeader>
              </Container>
            </Container>
          </Container>
        </Container>
        <Container row center customStyles={{ fontSize: 14, fontWeight: 600 }}>
          <Tag>
            <SubText>Has Garden</SubText>
          </Tag>
        </Container>
      </Container>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "purple",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <MailIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          //   middle
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Latest tickets
          </SubHeader>
          <CaptionText>View recent ticket on Barcelona</CaptionText>
        </Container>
      </Container>
      <Container
        row
        space="space-between"
        customStyles={{ flexWrap: "wrap", paddingTop: 20 }}
      >
        {[
          {
            id: 1,
            title: "water sink Broken",
            date: "Sat, Mar. 28, 2020",
            means: "mobile",
            description:
              "Happy Saturday, MailCharts! Time to come back to Netflix..."
          },
          {
            id: 2,
            title: "Door handle Broken",
            date: "Mon,     Dec. 14, 2019",
            means: "System",
            description: "MailCharts, Ozark Season 3 is now on Netflix"
          },
          {
            id: 3,
            title: "Water tab mulfunction",
            date: "Wed, Jun. 05, 2019",
            means: "mobile",
            description:
              "MailCharts, come back today to more TV shows & movies."
          },
          {
            id: 4,
            title: "Door handle Broken",
            date: "Mon,     Dec. 14, 2019",
            means: "System",
            description: "MailCharts, Ozark Season 3 is now on Netflix"
          }
        ].map(issue => {
          return <MiniTicketItem key={issue.id} ticket={issue} />;
        })}
      </Container>
    </Container>
  );
};

export default Overview;
