import React from "react";
import {
  Container,
  Text,
  CommandButton,
  PrimaryButton,
  SearchInput,
  OutlinedButton,
  SubText,
  ActionButton,
  IconButton,
  Header,
  CaptionText,
  Input,
  TextInput,
} from "../../styledComponents";
import SearchHouses from "../../queryComponents/SearchHouses";
import styled from "styled-components";
import HouseItem from "../HouseItem";
import CreateHouse from "./create/CreateHouse";
import { Divider, Icon } from "@material-ui/core";
import IncreaseIcon from "@material-ui/icons/ArrowDropUp";
import DecreaseIcon from "@material-ui/icons/ArrowDropDown";

const options = [
  {
    key: 1,
    text: "Fruits",
  },
  { key: 2, text: "Apple" },
  { key: 3, text: "Banana" },
  { key: 4, text: "Orange", disabled: true },
  { key: 5, text: "Grape" },
  { key: 6, text: "-" },
  {
    key: 7,
    text: "Vegetables",
  },
  { key: 8, text: "Broccoli" },
  { key: 9, text: "Carrot" },
  { key: 10, text: "Lettuce" },
];
const SearchWrapper = styled(Container)`
  display: flex;
  width: 100%;
  flex-direction: row;
  padding: 20px 110px 10px 110px;
  /* background-color: white; */
`;
const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;

  width: 100%;
  padding-top: 0px;
  padding-left: 110px;
  padding-bottom: 0px;
  padding-right: 110px;
`;
const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const SearchBox = styled(Container)`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding-right: 10px;
  background-color: white;
  height: 32px;
  padding: 0px 5px;
  min-width: 150px;
  margin-left: 7px;
  margin-right: 10px;
`;

const Houses = (props) => {
  const [showNumber, setNumber] = React.useState(10);
  const [loading, setLoading] = React.useState(false);
  const [foundHouses, setFound] = React.useState(null);
  return (
    <Container flex={1}>
      <SearchWrapper>
        <Header>Your Houses</Header>
        <Container row center flex={1} right>
          <SubText>Search:</SubText>
          <SearchHouses />
          <CreateHouse />
        </Container>
      </SearchWrapper>
      <Wrapper>
        <Container
          space="space-between"
          center
          row
          customStyles={{ paddingTop: 10, paddingBottom: 10 }}
        >
          <Container row center flex={1}></Container>
        </Container>
        {foundHouses === null && (
          <Container flex={1} row customStyles={{ flexWrap: "wrap" }}>
            {props.houses &&
              props.houses.map((house) => {
                return <HouseItem key={house.id} house={house} {...props} />;
              })}
          </Container>
        )}
        {foundHouses !== null && (
          <Container flex={1} row customStyles={{ flexWrap: "wrap" }}>
            {foundHouses.map((house) => {
              return <HouseItem key={house.id} house={house} {...props} />;
            })}
          </Container>
        )}
      </Wrapper>
    </Container>
  );
};

export default Houses;

