import React from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  IconButton,
  Text,
  Input,
  TextInput,
  SubText,
  CaptionText,
  PrimaryButton,
  Header
} from "../../../styledComponents";
import PhotoIcon from "@material-ui/icons/CameraEnhance";
import HouseIcon from "@material-ui/icons/Home";
import { withStyles, makeStyles, useTheme } from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar
} from "@material-ui/core";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";
import Add from "@material-ui/icons/Add";

const useStyles = makeStyles(theme => ({
  dialogPaper: {
    height: 700,
    width: 525,
    borderRadius: 2,
    position: "relative"
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none"
  },
  dialogActions: {
    position: "absolute",
    width: "100%",
    bottom: 0,
    padding: 0,
    margin: 0,
    border: "none"
  },
  dialogContentStyle: {
    display: "flex",
    padding: 0,
    border: "none"
  },
  avatarStyle: {
    position: "relative",
    width: theme.spacing(12),
    height: theme.spacing(10),
    borderRadius: 5,
    backgroundColor: "#F3F2F1"
  },
  inputStyle: {
    border: 0,
    borderRadius: 4,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 60,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF"
    }
  }
}));

const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  /* position: absolute;
  top: 0;
  right: 0; */
  z-index: 1304;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;
const Form = styled.form`
  display: flex;
  flex-direction: row;
  padding: 15px 20px;
  /* background-color: red; */
`;

const CreateBuilding = () => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [scroll, setScroll] = React.useState("body");
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <IconButton
        onClick={handleClickOpen}
        customStyles={{
          border: "1px solid #E1E1E1",
          height: 33,
          width: 33
        }}
      >
        <Add style={{ fontSize: 18 }} />
      </IconButton>

      <Dialog
        fullScreen={fullScreen}
        onClose={handleClose}
        classes={{
          paper: classes.dialogPaper
        }}
        aria-labelledby="customized-dialog-title"
        open={open}
        scroll={scroll}
      >
        <DialogTitle
          id="scroll-dialog-title"
          classes={{
            root: classes.dialogActionStyle
          }}
        >
          <Container
            row
            center
            right
            color="transparent"
            space="space-between"
            customStyles={{
              paddingTop: 15,
              paddingBottom: 7,
              paddingRight: 10,
              paddingLeft: 20
            }}
          >
            <CloseBtn onClick={handleClickOpen}>
              <CloseIcon style={{ fontSize: 23 }} />
            </CloseBtn>
          </Container>
        </DialogTitle>
        <DialogContent
          dividers={scroll === "paper"}
          classes={{
            root: classes.dialogContentStyle
          }}
        >
          <Container
            flex={1}
            column
            color="transparent"
            customStyles={{ paddingTop: 10 }}
          >
            <Container color="transparent" column middle center>
              <Header>Add House to a building</Header>
              <CaptionText>You are adding 1 house </CaptionText>
            </Container>
            <Form>
              <fieldset
                style={{
                  display: "flex",
                  paddingRight: 0,
                  paddingLeft: 0,
                  paddingBottom: 10,
                  paddingTop: 20,
                  border: "none"
                }}
              >
                <Container row color="transparent" flex={1}>
                  <Container
                    flex={1}
                    column
                    middle
                    color="transparent"
                    customStyles={{ paddingRight: 10 }}
                  >
                    <Input placeholder="Create a new building" type="text" />
                  </Container>
                  <Container flex={false} column color="transparent">
                    <PrimaryButton>CREATE</PrimaryButton>
                  </Container>
                </Container>
              </fieldset>
            </Form>
          </Container>
        </DialogContent>
        <DialogActions
          classes={{
            root: classes.dialogActions
          }}
        >
          <Container
            flex={1}
            row
            center
            space="space-between"
            customStyles={{ padding: 10 }}
          >
            <CancelBtn onClick={handleClose}>Cancel</CancelBtn>
            <PrimaryButton onClick={handleClose}>Add House</PrimaryButton>
          </Container>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default CreateBuilding;
