import React from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  IconButton,
  Text,
  Input,
  TextInput,
  SubText,
  CaptionText,
  PrimaryButton,
  Header,
  SubHeader,
} from "../../styledComponents";
import userPhoto from "../../../public/static/sandy.jpeg";
import AdjustIcon from "@material-ui/icons/Adjust";
// import HouseIcon from "@material-ui/icons/Home";
import HouseIcon from "@material-ui/icons/Kitchen";
import {
  withStyles,
  makeStyles,
  useTheme,
  fade,
} from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar,
  Divider,
} from "@material-ui/core";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";
import Add from "@material-ui/icons/Add";
import TicketItem from "../TicketItem";

const useStyles = makeStyles((theme) => ({
  dialogPaper: {
    height: 700,
    minWidth: "40%",
    borderRadius: 2,
    backgroundColor: "white",
    position: "relative",
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogActions: {
    position: "absolute",
    width: "100%",
    bottom: 0,
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogContentStyle: {
    display: "flex",
    flex: 1,
    height: "100%",
    padding: 0,
    border: "none",
    backgroundColor: "white",
  },
  avatarStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 1,
    backgroundColor: "#F3F2F1",
  },
  buildingImageStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 2,
    marginRight: 20,
    border: "1.5px dashed #F3F2F1",
    backgroundColor: "#F3F2F1",
  },
  inputStyle: {
    border: 0,
    borderRadius: 4,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 60,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF",
    },
  },
}));

const Wrapper = styled(Container)`
  flex: 1;
  flex-direction: column;
  height: 100%;
  padding: 15px;
  /* background-color: red; */
`;
const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  /* position: absolute;
  top: 0;
  right: 0; */
  z-index: 1304;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const AddBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;
const Form = styled.form`
  display: flex;
  flex-direction: row;
  padding: 0px 40px;
  /* background-color: red; */
`;

const SingleTicket = ({ list, ticket, button, open, handleClose }) => {
  const classes = useStyles();
  const theme = useTheme();
  const [scroll, setScroll] = React.useState("paper");
  const fullScreen = useMediaQuery(theme.breakpoints.down("md"));
  const [hover, setHover] = React.useState(false);

  const handleHover = () => {
    setHover(true);
  };
  const handleHoverOut = () => {
    setHover(false);
  };

  let house = ticket !== null ? ticket.houseId : "";
  console.log(ticket);
  // const { reported_by } = ticket;
  return (
    <div style={{ flex: 0 }}>
      <TicketItem
        list
        ticket={ticket}
        onClick={() => {
          handleClickOpen();
          setTicket(issue);
        }}
      />
      <Dialog
        fullScreen={fullScreen}
        onClose={handleClose}
        classes={{
          paper: classes.dialogPaper,
        }}
        open={open}
        scroll={scroll}
      >
        <DialogTitle
          id="scroll-dialog-title"
          classes={{
            root: classes.dialogActionStyle,
          }}
        >
          <Container
            row
            center
            color="transparent"
            space="space-between"
            customStyles={{
              paddingTop: 15,
              paddingBottom: 7,
              paddingRight: 10,
              paddingLeft: 20,
            }}
          >
            <Container row center flex={0} color="transparent">
              <HouseIcon style={{ fontSize: 30, marginRight: 10 }} />
              <SubHeader></SubHeader>
            </Container>
            <CloseBtn onClick={() => handleClose()}>
              <CloseIcon style={{ fontSize: 23 }} />
            </CloseBtn>
          </Container>
        </DialogTitle>
        <DialogContent
          classes={{
            root: classes.dialogContentStyle,
          }}
        >
          {ticket !== null && (
            <Wrapper>
              <Container
                flex={0}
                row
                color="transparent"
                customStyles={{ paddingTop: 15 }}
              >
                <Container flex={0.1} row center middle color="transparent">
                  <HouseIcon style={{ fontSize: 20 }} />
                </Container>
                <Container flex={0.9} row center color="transparent">
                  <Text>House</Text>
                </Container>
              </Container>

              <Container
                flex={0}
                row
                color="transparent"
                customStyles={{ paddingTop: 5 }}
              >
                <Container flex={0.1} row center middle color="transparent" />

                <Container flex={0.9} row center color="transparent">
                  <CaptionText>{ticket.title}</CaptionText>
                </Container>
              </Container>
              <Container
                flex={0}
                row
                color="transparent"
                customStyles={{ paddingTop: 15 }}
              >
                <Container flex={0.1} row center middle color="transparent">
                  <AdjustIcon />
                </Container>
                <Container flex={0.9} row center color="transparent">
                  <Text>Title</Text>
                </Container>
              </Container>
              <Container
                flex={0}
                row
                color="transparent"
                customStyles={{ paddingTop: 5 }}
              >
                <Container flex={0.1} row center middle color="transparent" />

                <Container flex={0.9} row center color="transparent">
                  <CaptionText>{ticket.title}</CaptionText>
                </Container>
              </Container>
              <Container
                flex={0}
                row
                color="transparent"
                customStyles={{ paddingTop: 15 }}
              >
                <Container flex={0.1} row center middle color="transparent">
                  <AdjustIcon />
                </Container>
                <Container flex={0.9} row center color="transparent">
                  <Text>Description</Text>
                </Container>
              </Container>
              <Container
                flex={0}
                row
                color="transparent"
                customStyles={{ paddingTop: 5 }}
              >
                <Container flex={0.1} row center middle color="transparent" />

                <Container flex={0.9} row center color="transparent">
                  <CaptionText>{ticket.description}</CaptionText>
                </Container>
              </Container>
            </Wrapper>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default SingleTicket;
