import React from "react";
import Link from "next/link";
import {
  Container,
  Text,
  SubHeader,
  Input,
  PrimaryButton,
  SubText,
  ActionButton,
  CaptionText
} from "../styledComponents";
import styled from "styled-components";
import { device } from "../../lib/deviceSize";
import hasLogo from "../../public/static/HAS.png";

const ImgWrapper = styled(Container)`
  flex-direction: column;
  flex: 0.3;
  padding-top: 50px;
  padding-left: 60px;
  padding-right: 10px;

  background-color: ${({ theme }) => theme.btnPrimary};
  min-height: 100%;
  max-height: 100%;
`;
const FormWrapper = styled(Container)`
  flex-direction: column;
  padding-top: 40px;
  padding-left: 60px;
  padding-right: 60px;
  flex: 0.7;
  min-height: 100%;
  max-height: 100%;
`;
const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 55%;

  flex: 0.8;
  /* background-color: red; */
`;

const Register = () => {
  return (
    <Container
      flex={1}
      row
      customStyles={{ minHeight: "100vh", maxHeight: "100vh" }}
    >
      <FormWrapper>
        <Container flex={false} row center color="transparent">
          <img
            src={hasLogo}
            style={{ width: "50px", height: "50px", marginRight: "10px" }}
          />
          <Text>House-Assist Space</Text>
        </Container>
        <Container flex={1} column middle center>
          <Form>
            <SubHeader customStyles={{ fontWeight: "500", marginBottom: 10 }}>
              Create your H-A-Space account
            </SubHeader>
            <Text>
              Ready to handle your property? House-Assist Space assists your
              plan and organize your houses and properties.
            </Text>
            <fieldset
              style={{
                display: "flex",
                paddingTop: "30px",
                paddingLeft: 0,
                paddingRight: 0,
                paddingBottom: 0,
                border: "none",
                flexDirection: "column",
                marginBottom: 10,
                marginTop: 10
              }}
            >
              <Input label="YOUR EMAIL" placeholder="eg: name@example.com" />
            </fieldset>

            <PrimaryButton type="submit" customStyles={{ padding: 12 }}>
              Register
            </PrimaryButton>
            <Container row middle center customStyles={{ marginTop: 40 }}>
              <SubText
                customStyles={{
                  fontWeight: "italic"
                }}
              >
                Already have an account?
              </SubText>
              <Link href={"/Landlrd/signup"} as={"landlord/register"}>
                <a>
                  <SubText
                    customStyles={{
                      fontWeight: "italic",
                      color: "#0078D4",
                      marginLeft: 8
                    }}
                  >
                    Register
                  </SubText>
                </a>
              </Link>
            </Container>
          </Form>
        </Container>
      </FormWrapper>
      <ImgWrapper />
    </Container>
  );
};

export default Register;
