import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import DotIcon from "@material-ui/icons/FiberManualRecord";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GardenIcon from "@material-ui/icons/AmpStories";
import CarIcon from "@material-ui/icons/DirectionsCar";
import GraphIcon from "@material-ui/icons/ShowChart";
import ViewIcon from "@material-ui/icons/Style";
import MiniTicketItem from "./MiniTicketItem";
import MbanzaLogo from "../../../public/static/mbanza.png";
import SingleTicket from "../tickets/SingleTicket";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 2px 15px;
  margin: 3px 3px;
`;
const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  flex: 1;
  padding-top: 30px;
  max-height: calc(100vh - 115px);
  overflow: scroll;
  ::-webkit-scrollbar {
    display: none;
  }
`;

const Overview = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };

  const {
    house_type,
    managingAgent,
    house_status,
    appliances,
    ext_ft,
    utilities,
  } = house;
  return (
    <Wrapper>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <ViewIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            At a glance
          </SubHeader>
        </Container>
      </Container>

      <Container
        row
        flex={1}
        customStyles={{
          marginTop: 10,
          marginBottom: 20,
        }}
        color="transparent"
      >
        <Container
          flex={0.5}
          row
          customStyles={{
            borderRadius: 3,
            padding: 20,
            marginRight: 8,
          }}
          color="white"
        >
          <Container
            row
            middle
            flex={0.1}
            color="transparent"
            customStyles={{ height: "100%", paddingTop: 2 }}
          >
            <DotIcon style={{ color: "blue", fontSize: 18 }} />
          </Container>
          <Container
            column
            space={"space-between"}
            flex={0.8}
            color="transparent"
          >
            <Sub
              customStyles={{ fontSize: 16, marginBottom: 0, marginTop: 0 }}
            >
              Tenant
            </Sub>
            <DotIcon style={{ color: "blue", fontSize: 18 }} />
            <DotIcon style={{ color: "blue", fontSize: 18 }} />
          </Container>
          <Container
            row
            middle
            flex={0.1}
            color="transparent"
            customStyles={{ height: "100%", paddingTop: 2 }}
          >
            <DotIcon style={{ color: "blue", fontSize: 18 }} />
          </Container>
        </Container>
        <Container
          flex={0.5}
          customStyles={{
            borderRadius: 3,
            padding: 20,
            marginLeft: 8,
          }}
          color="white"
        >
          ddsdasdas
        </Container>
      </Container>

      <Container
        column
        flex={1}
        customStyles={{
          borderRadius: 3,
          padding: 15,
          marginTop: 10,
          marginBottom: 20,
        }}
        color="white"
      >
        <Container row center color="transparent">
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            Facts and features
          </SubHeader>
        </Container>
        <Container
          flex={1}
          // customStyles={{ paddingTop: 10 }}
          row
          center
          color="transparent"
        >
          <Container column flex={1} color="transparent">
            <Container row flex={1} color="transparent">
