import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import styled from "styled-components";
import { useRouter } from "next/router";
import { Avatar, Drawer } from "@material-ui/core";
import WithLayout from "../../../components/Landlrd/Layout";
import { theme as customTheme } from "../../../components/Page";
import {
  Container,
  CaptionText,
  SubText,
  Header
} from "../../../components/styledComponents";

import ActiveLink from "../../../components/Landlrd/ActiveLink";
import hasLogo from "../../../public/static/HAS.png";

const useStyles = makeStyles(theme => ({
  small: {
    width: theme.spacing(10),
    height: theme.spacing(10),
    borderRadius: 0,
    backgroundColor: "transparent",
    border: 0
  },

  menuButton: {
    marginRight: 36
  },
  hide: {
    display: "none"
  },

  drawerPaper: {
    paddingLeft: "100px",
    paddingRight: "100px",
    // height: 64,
    backgroundColor: "white"
  },

  content: {
    backgroundColor: customTheme.bgPrimary
  },
  smaller: {
    width: theme.spacing(7),
    height: theme.spacing(7)
  },
  userAvatar: {
    width: theme.spacing(4.5),
    height: theme.spacing(4.5)
  }
}));
const HeaderSection = styled(Container)`
  display: flex;
  width: 100%;
  flex-direction: row;
  padding-top: 30px;
  padding-left: 110px;
  padding-right: 110px;
  padding-bottom: 20px;
  background-color: white;
`;
const ContentSection = styled(Container)`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 0px 110px;
  background-color: red;
`;
const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;

const House = Page => {
  return class extends React.Component {
    static getInitialProps(ctx) {
      if (Page.getInitialProps) return Page.getInitialProps(ctx);
    }
    render() {
      return (
        <Container flex={1}>
          <HeaderSection>
            <Container flex={false} color="transparent">
              <Avatar src={hasLogo} className={classes.small} />
            </Container>
            <Container
              flex={1}
              column
              customStyles={{ paddingLeft: 40, paddingTop: 5 }}
              color="transparent"
            >
              <Header customStyles={{ fontWeight: 400, marginBottom: 0 }}>
                Barcelona
              </Header>
              <SubText customStyles={{ color: "#979a9a" }}>
                KGN606 St. KIGALI, GA 30338, RWANDA
              </SubText>
              <Container
                column
                customStyles={{ paddingTop: 7, paddingBottom: 10 }}
                color="transparent"
              >
                <SubText>last updated 20-March-2020</SubText>
              </Container>
              <Container
                row
                customStyles={{ paddingTop: 10, paddingBottom: 10 }}
                color="transparent"
              >
                <Tag>
                  <CaptionText>has Tenant</CaptionText>
                </Tag>
              </Container>
            </Container>
          </HeaderSection>
          <Container
            flex={1}
            column
            customStyles={{ position: "relative" }}
            id={"tab-container"}
          >
            <Drawer
              variant="permanent"
              anchor="top"
              classes={{
                paper: classes.drawerPaper
              }}
              PaperProps={{
                style: {
                  position: "absolute",
                  border: "none"
                }
              }}
              BackdropProps={{ style: { position: "absolute" } }}
              ModalProps={{
                container: ` ${
                  typeof window !== "undefined"
                    ? document.getElementById("tab-container")
                    : ""
                }`,
                style: { position: "absolute" }
              }}
            >
              <Container
                center
                row
                color="transparent"
                customStyles={{ height: 64 }}
              >
                <Container
                  row
                  flex={1}
                  center
                  color="transparent"
                  customStyles={{ marginLeft: 20 }}
                >
                  <ActiveLink label="Dashboard" href={"/Landlrd/dashboard"} />
                  <ActiveLink label="Houses" href={"/Landlrd/houses"} />
                  <ActiveLink label="Tenants" href={"/Landlrd/tenants"} />
                  <ActiveLink label="Tickets" href={"/Landlrd/tickets"} />
                </Container>
              </Container>
            </Drawer>
            <Container
              flex={1}
              column
              customStyles={{
                paddingTop: 64
              }}
            >
              <Page {...props} />
            </Container>
          </Container>
        </Container>
      );
    }
  };
};

export default WithLayout(House);

