import React, { useState } from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  IconButton,
  Text,
  Input,
  TextInput,
  SubText,
  CaptionText,
  PrimaryButton,
  Header,
  SubHeader,
} from "../../styledComponents";
import gql from "graphql-tag";
import { ApolloConsumer, Mutation } from "react-apollo";
import { useMutation } from "@apollo/react-hooks";
import userPhoto from "../../../public/static/sandy.jpeg";
import CheckIcon from "@material-ui/icons/Done";
import HouseIcon from "@material-ui/icons/Home";
import { withStyles, makeStyles, useTheme } from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar,
  Divider,
} from "@material-ui/core";
import { GET_USER_PARENT_HOUSES } from "../../queryComponents/ParentHouses";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";
import Add from "@material-ui/icons/Add";
import loadingImg from "../../../public/static/126.gif";

const useStyles = makeStyles((theme) => ({
  dialogPaper: {
    height: 700,
    width: 525,
    borderRadius: 2,
    backgroundColor: "white",
    position: "relative",
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none",
    backgroundColor: "white",
  },
  dialogActions: {
    position: "absolute",
    width: "100%",
    bottom: 0,
    padding: 10,
    margin: 0,
    border: "none",
    // backgroundColor: "red",
  },
  dialogContentStyle: {
    display: "flex",
    padding: 0,
    border: "none",
    backgroundColor: "white",
  },
  avatarStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 1,
    backgroundColor: "#F3F2F1",
  },
  buildingImageStyle: {
    width: theme.spacing(5),
    height: theme.spacing(5),
    borderRadius: 2,
    marginRight: 20,
    border: "1.5px dashed #F3F2F1",
    backgroundColor: "#F3F2F1",
  },
  inputStyle: {
    border: 0,
    borderRadius: 4,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 10,
    backgroundColor: "#F5F8FA",
    // lineHeight: 2,
    minHeight: 60,
    fontSize: 12,
    "&:focus": {
      border: "1px solid #4C3AF7",
      backgroundColor: "#FEFEFF",
    },
  },
}));

const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  /* position: absolute;
  top: 0;
  right: 0; */
  z-index: 1304;
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const AddBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 2px;
`;
const Form = styled.form`
  display: flex;
  flex-direction: row;
  padding: 0px 40px;
  /* background-color: red; */
`;

const CREATE_PARENT_HOUSE_MUTATION = gql`
  mutation CREATE_PARENT_HOUSE_MUTATION($building_name: String!) {
    createParentHouse(building_name: $building_name) {
      id
      building_name
      address
      image
    }
  }
`;

const ADD_HOUSE_TO_BUILDING = gql`
  mutation AddHouseToBuilding(
    $houseId: ID!
    $buildingId: ParentHouseUpdateOneInput!
  ) {
    addHouseToBuilding(houseId: $houseId, buildingId: $buildingId) {
      id
      house_heading
    }
  }
`;

const AddHouseTenant = ({ house }) => {
  const classes = useStyles();
  const theme = useTheme();
  const [building_name, setBuildingName] = useState(null);
  const [open, setOpen] = useState(false);
  const [scroll, setScroll] = useState("paper");
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));
  const [isloading, setLoading] = useState(false);
  const [addToBuilding, setAddToBuilding] = useState([]);
  const [buildingList, setBuildingList] = useState(null);

  const [addTo, { data }] = useMutation(ADD_HOUSE_TO_BUILDING);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  async function getBuildings(client) {
    setLoading(true);
    const res = await client.query({ query: GET_USER_PARENT_HOUSES });
    const buildings = res.data.getUserParentHouses;
    setBuildingList(buildings);
    setLoading(false);
  }
  return (
    <ApolloConsumer>
      {(client) => (
        <div>
          <IconButton
            onClick={(e) => {
              handleClickOpen();
              getBuildings(client);
            }}
            customStyles={{
              backgroundColor: "#0078D4",
              height: 33,
              //   width: 33,
            }}
          >
            <Add style={{ fontSize: 18, color: "white" }} />
            Add Tenant
          </IconButton>

          <Dialog
            fullScreen={fullScreen}
            onClose={(e) => {
              handleClose();
            }}
            classes={{
              paper: classes.dialogPaper,
            }}
            aria-labelledby="customized-dialog-title"
            open={open}
            scroll={scroll}
          >
            <DialogTitle
              id="scroll-dialog-title"
              classes={{
                root: classes.dialogActionStyle,
              }}
            >
              <Container
                row
                center
                right
                color="transparent"
                space="space-between"
                customStyles={{
                  paddingTop: 15,
                  paddingBottom: 7,
                  paddingRight: 10,
                  paddingLeft: 20,
                }}
              >
                <Container color="transparent" column row center left flex={1}>
                  <SubHeader>Add House</SubHeader>
                </Container>
                <CloseBtn onClick={handleClose}>
                  <CloseIcon style={{ fontSize: 23 }} />
                </CloseBtn>
              </Container>
            </DialogTitle>
            <DialogContent
              classes={{
                root: classes.dialogContentStyle,
              }}
            >
              <Container
                flex={1}
                column
                color="transparent"
                customStyles={{ paddingTop: 10, paddingBottom: 10 }}
              ></Container>
            </DialogContent>
            <DialogActions className={classes.dialogActions}>
              <PrimaryButton
                customStyles={{
                  pointerEvents: addToBuilding.length <= 0 ? "none" : "auto",
                  opacity: addToBuilding.length <= 0 ? 0.4 : 1.0,
                }}
                onClick={() => {
                  addTo({
                    variables: {
                      houseId: house.id,
                      buildingId: addToBuilding[0].id,
                    },
                  });
                }}
              >
                SAVE
              </PrimaryButton>
            </DialogActions>
          </Dialog>
        </div>
      )}
    </ApolloConsumer>
  );
};

export default AddHouseTenant;
