import React from "react";
import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import {
  Container,
  Text,
  IconButton,
  SubHeader,
  SubText,
  CaptionText,
  NormalText
} from "../styledComponents";
import userPhoto from "../../public/static/sandy.jpeg";
import { Avatar, Divider, Chip } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
const useStyles = makeStyles(theme => ({
  small: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    borderRadius: 0
  }
}));
const HouseWrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  padding-top: 15px;
  padding-left: 15px;
  padding-right: 15px;
  height: 380px;
  min-width: 320px;
  border: 1px solid #d5d8dc;
  position: relative;
  margin-bottom: 20px;
  border-radius: 2px;
  background-color: white;
  cursor: pointer;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;

const Tag = styled(Container)`
  display: flex;
  flex: 1;
  border-radius: 10px;
  padding: 5px 10px;
  margin: 0;
`;
const CardHeader = styled(Container)`
  direction: flex;
  flex-direction: row;
  width: 100%;
  align-items: center;
  background-color: transparent;
`;

const ImageWrapper = styled(Container)`
  direction: flex;
  flex-direction: row;
  flex: 0;
  width: 100%;
  /* align-items: center; */
  background-color: transparent;
  padding-top: 15px;
  padding-left: 0px;
  padding-right: 0px;
  padding-bottom: 10px;
`;

const HouseItem = () => {
  const classes = useStyles();
  return (
    <HouseWrapper>
      <CardHeader>
        <Avatar src={userPhoto} className={classes.small} />
        <Container
          flex={1}
          column
          color="transparent"
          customStyles={{ paddingLeft: 10, paddingRight: 10 }}
        >
          <SubHeader customStyles={{ fontSize: "18px", fontWeight: "500" }}>
            Barcelona
          </SubHeader>
          <CaptionText customStyles={{ color: "#0078D4" }}>
            Rwanda, Kigali, KNG 602 St.
          </CaptionText>
        </Container>

        <Container flex={false} color="transparent">
          <IconButton
            customStyles={{
              border: "1px solid #E1E1E1",
              height: 33,
              width: 33
            }}
          >
            <AddIcon />
          </IconButton>
        </Container>
      </CardHeader>
      <CaptionText customStyles={{ marginTop: 3 }}>
        Last updated 2 days ago..
      </CaptionText>
      <ImageWrapper>
        <Container flex={1}>
          <Avatar
            src={userPhoto}
            style={{ width: "100%", height: "auto", borderRadius: 0 }}
          />
        </Container>
        <Container flex={1}>
          <Avatar
            src={userPhoto}
            style={{ width: "100%", height: "auto", borderRadius: 0 }}
          />
        </Container>
      </ImageWrapper>

      <Container color="transparent" column>
        <Container flex={1} row color="transparent">
          <Container flex={1} column middle color="transparent">
            <CaptionText>BEDROOMS</CaptionText>
            <SubText customStyles={{ fontWeight: "600" }}>3 </SubText>
          </Container>
          <Container flex={1} column middle color="transparent">
            <CaptionText>BATHROOMS</CaptionText>
            <SubText customStyles={{ fontWeight: "600" }}>3</SubText>
          </Container>
        </Container>
        <Container flex={1} row color="transparent">
          <Container flex={1} column middle color="transparent">
            <CaptionText>ADMIN FEE</CaptionText>
            <SubText customStyles={{ fontWeight: "600" }}>$2000 </SubText>
          </Container>
          <Container flex={1} column middle color="transparent">
            <CaptionText>BROKERAGE FEE</CaptionText>
            <SubText customStyles={{ fontWeight: "600" }}>$2000</SubText>
          </Container>
        </Container>
      </Container>
      <Container
        flex={false}
        row
        center
        customStyles={{ paddingTop: 5, paddingBottom: 5, width: "100%" }}
        color="transparent"
      >
        <Tag>Has tenant</Tag>
        <Tag>has balcony</Tag>
        <Tag>has hot-water</Tag>
      </Container>
      <Divider style={{ width: "100%", height: "1px" }} />
      <Container
        row
        color="transparent"
        customStyles={{
          flex: 1,
          position: "absolute",
          bottom: 0,
          paddingTop: 6,
          paddingBottom: 6
        }}
      >
        <NormalText>Has had 3 tenants since May 2015</NormalText>
      </Container>
    </HouseWrapper>
  );
};

export default HouseItem;
