import React from "react";
import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import { Container, Text, IconButton, SubHeader, SubText } from "../styledComponents";
import userPhoto from "../../public/static/sandy.jpeg";
import { Avatar } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
const useStyles = makeStyles(theme => ({
  small: {
    width: theme.spacing(4),
    height: theme.spacing(4),
    borderRadius: 0
  }
}));
const HouseWrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  /* align-items: center;
  justify-content: space-between; */
  height: 350px;
  min-width: 320px;
  border: 1px solid #d5d8dc;
  margin-bottom: 20px;
  border-radius: 2px;
  background-color: white;
  &:hover {
    box-shadow: 0 3.2px 7.2px 0 rgba(0, 0, 0, 0.132),
      0 0.6px 1.8px 0 rgba(0, 0, 0, 0.108);
  }
`;

const CardHeader = styled(Container)`
  direction: flex;
  flex-direction: row;
  padding: 15px;
  width: 100%;
  align-items: center;
  background-color: transparent;
`;
const HouseItem = () => {
  const classes = useStyles();
  return (
    <HouseWrapper>
      <CardHeader>
        <Avatar src={userPhoto} className={classes.small} />
        <Container
          flex={1}
          column
          color="transparent"
          customStyles={{ paddingLeft: 10, paddingRight: 10 }}
        >
          <SubHeader customStyles={{ fontSize: "18px", fontWeight: "500" }}>
            Barcelona
          </SubHeader>
          <SubText>Rwanda, Kigali, KNG 602 St.</SubText>
        </Container>

        <Container flex={false} color="transparent">
          <IconButton
            customStyles={{
              border: "1px solid #E1E1E1",
              height: 33,
              width: 33
            }}
          >
            <AddIcon />
          </IconButton>
        </Container>
      </CardHeader>
      <Text>Image</Text>
      <Text>Description</Text>
      <Text>Footer</Text>
    </HouseWrapper>
  );
};

export default HouseItem;
