import React from "react";
import styled, { css } from "styled-components";
import { SubText, CaptionText } from "./Text";
import Container from "./Container";

const Input = styled.input`
  border-bottom: 1px solid #d7dbdd;
  border-top: none;
  border-left: none;
  border-right: none;
  padding: 10px;
  background-color: transparent;
  font-size: 12px;
  &:focus {
    border-bottom: 1.6px solid #5b86c6;
    /* background-color: #e5e7e9; */
  }
  ${props => props.customStyles};
`;

const TextInput = ({ label, row, ...other }) => {
  return (
    <Container
      customStyles={{ marginBottom: 10, flexDirection: row ? "row" : "column",
      justifyContent
     }}
      color="transparent"
    >
      <CaptionText customStyles={{ marginBottom: 5 }}>{label}</CaptionText>
      <Input {...other} />
    </Container>
  );
};

export default TextInput;
