import React from "react";
import styled from "styled-components";
import { fade } from "@material-ui/core/styles";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  NormalText,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import PhoneIcon from "@material-ui/icons/LocalPhone";
import TenantIcon from "@material-ui/icons/PersonPin";
import BusinessIcon from "@material-ui/icons/Business";
import ArrowIcon from "@material-ui/icons/ArrowForward";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GardenIcon from "@material-ui/icons/AmpStories";
import CarIcon from "@material-ui/icons/DirectionsCar";
import GraphIcon from "@material-ui/icons/ShowChart";
import ViewIcon from "@material-ui/icons/Style";
import MiniTicketItem from "./MiniTicketItem";
import MbanzaLogo from "../../../public/static/mbanza.png";
import SingleTicket from "../tickets/SingleTicket";
import CurrentTenant from "../../queryComponents/landlord/CurrentTenant";
import AddHouseTenant from "./AddHouseTenant";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 2px 15px;
  margin: 3px 3px;
`;
const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  flex: 1;
  padding-top: 30px;
  max-height: calc(100vh - 115px);
  overflow: scroll;
  ::-webkit-scrollbar {
    display: none;
  }
`;

const TenantCard = ({ house, tenant }) => {
  return (
    <CurrentTenant houseId={house.id}>
      {({ data, loading, error }) => {
        if (data) {
          const tenants = data.getCurrentTenant;
          console.log(tenants);
          return (
            <Container
              flex={0.5}
              row
              customStyles={{
                borderRadius: 3,
                paddingTop: 20,
                paddingLeft: 20,
                paddingRight: 0,
                paddingBottom: 20,
                marginRight: 8,
              }}
              color="white"
            >
              <Container
                row
                middle
                flex={0.1}
                color="transparent"
                customStyles={{ height: "100%" }}
              >
                <TenantIcon style={{ fontSize: 20 }} />
              </Container>
              <Container
                column
                space={"space-between"}
                flex={0.8}
                color="transparent"
              >
                <SubText
                  customStyles={{
                    fontSize: 16,

                    marginBottom: 0,
                    marginTop: 0,
                  }}
                >
                  Tenant
                </SubText>

                {tenants.map((curr) => {
                  return (
                    <React.Fragment key={curr.id}>
                      <Container
                        color="transparent"
                        customStyles={{ paddingTop: 10, paddingBottom: 10 }}
                      >
                        <Container row center>
                          {curr.profileImage === null ? (
                            <Avatar
                              style={{
                                width: 30,
                                height: 30,
                                marginRight: 8,
                              }}
                            >
                              <SubHeader
                                customStyles={{ fontSize: 10 }}
                              >

                              </SubHeader>
                            </Avatar>
                          ) : (
                            <Avatar
                              source={curr.profileImage}
                              style={{
                                backgroundColor: fade("#F7DC6F", 0.3),
                                width: 30,
                                height: 30,
                                marginRight: 8,
                              }}
                            />
                          )}
                          <SubHeader
                            customStyles={{
                              fontSize: 18,
                            }}
                          >
                            {curr.firstName + " " + curr.lastName}
                          </SubHeader>
                        </Container>
                      </Container>
                      <Container
                        row
                        center
                        color="transparent"
                        customStyles={{ paddingTop: 5, paddingBottom: 5 }}
                      >
                        <Avatar
                          style={{
                            backgroundColor: fade("#F7DC6F", 0.3),
                            width: 25,
                            height: 25,
                            marginRight: 8,
                          }}
                        >
                          <MailIcon
                            style={{ fontSize: 15, color: "#F1C40F" }}
                          />
                        </Avatar>
                        <SubText>{curr.comm_email}</SubText>
                      </Container>
                      <Container
                        row
                        center
                        color="transparent"
                        customStyles={{ paddingTop: 1, paddingBottom: 1 }}
                      >
                        <Avatar
                          style={{
                            backgroundColor: fade("#58D68D", 0.3),
                            width: 25,
                            height: 25,
                            marginRight: 8,
                          }}
                        >
                          <PhoneIcon
                            style={{ fontSize: 15, color: "#27AE60" }}
                          />
                        </Avatar>
                        <SubText>{curr.tel_no}</SubText>
                      </Container>
                    </React.Fragment>
                  );
                })}
              </Container>
              {tenants.length <= 0 && <AddHouseTenant house={house} />}
            </Container>
          );
        }
        return null;
      }}
    </CurrentTenant>
  );
};

const ManagerCard = ({ managingAgent }) => {
  return (
    <React.Fragment>
      {managingAgent === null ? (
        <Container
          flex={0.5}
          row
          customStyles={{
            borderRadius: 3,
            padding: 20,
            marginRight: 8,
          }}
          color="white"
        >
          <Container
            row
            middle
            flex={0.1}
            color="transparent"
            customStyles={{ height: "100%" }}
          >
            <BusinessIcon style={{ fontSize: 20 }} />
          </Container>
          <Container
            column
            space={"space-between"}
            flex={0.8}
            color="transparent"
          >
            <SubText
              customStyles={{
                fontSize: 16,

                marginBottom: 0,
                marginTop: 0,
              }}
            >
              Managing Agent
            </SubText>
          </Container>
          <Container
            row
            middle
            flex={0.1}
            color="transparent"
            customStyles={{ height: "100%", paddingTop: 2 }}
          >
            <ArrowIcon style={{ fontSize: 18 }} />
          </Container>
        </Container>
      ) : (
        <Container
          flex={0.5}
          row
          customStyles={{
            borderRadius: 3,
            padding: 20,
            marginRight: 8,
          }}
          color="white"
        >
          <Container
            row
            middle
            flex={0.1}
            color="transparent"
            customStyles={{ height: "100%" }}
          >
            <BusinessIcon style={{ fontSize: 20 }} />
          </Container>
          <Container
            column
            space={"space-between"}
            flex={0.8}
