import React from "react";
import {
  Container,
  Text,
  CommandButton,
  PrimaryButton,
  SearchInput,
  OutlinedButton,
  ActionButton
} from "../../styledComponents";
import styled from "styled-components";
import TicketItem from "../TicketItem";
import SingleTicket from "./SingleTicket";

const useStyles = makeStyles(theme => ({
  small: {
    width: theme.spacing(10),
    height: theme.spacing(10),
    borderRadius: 0,
    backgroundColor: "transparent",
    border: 0
  },

  smaller: {
    width: theme.spacing(7),
    height: theme.spacing(7)
  },
  userAvatar: {
    width: theme.spacing(4.5),
    height: theme.spacing(4.5)
  },
  root: {
    flexGrow: 1
  },
  padding: {
    padding: theme.spacing(3)
  }
}));

const SearchWrapper = styled(Container)`
  display: flex;
  width: 100%;
  flex-direction: row;
  padding: 30px 110px;
  background-color: white;
`;
const ContentSection = styled(Container)`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 0px 110px;
  background-color: white;
`;
const TabSection = styled(Container)`
  display: flex;
  flex: 1;
  flex-direction: column;
  width: 100%;
  padding: 0px 110px;
`;
const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding: 0px 110px;
`;
const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <Paper
      component={"div"}
      role="tabpanel"
      elevation={0}
      hidden={value !== index}
      id={`vertical-tabpanel-${index}`}
      aria-labelledby={`vertical-tab-${index}`}
      {...other}
    >
      {value === index && <TabSection>{children}</TabSection>}
    </Paper>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired
};

const Tickets = props => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = ticket => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };
  return (
    <Container flex={1}>
      <SearchWrapper>
        <Container flex={1} color="transparent">
          <SearchInput
            placeholder="Search a maintenance issue"
            customStyles={{ width: "80%" }}
          />
        </Container>

        <PrimaryButton customStyles={{ width: 150, fontSize: 18 }}>
          Search
        </PrimaryButton>
      </SearchWrapper>
      <Wrapper>
        <Container
          right
          center
          row
          customStyles={{ paddingTop: 10, paddingBottom: 10 }}
        ></Container>
        <Container
          flex={1}
          row
          customStyles={{ flexWrap: "wrap", justifyContent: "space-between" }}
        >
          {[
            {
              id: 1,
              title: "water sink Broken",
              date: "Sat, Mar. 28, 2020",
              means: "mobile",
              description:
                "Happy Saturday, MailCharts! Time to come back to Netflix..."
            },
            {
              id: 2,
              title: "Door handle Broken",
              date: "Mon,     Dec. 14, 2019",
              means: "System",
              description: "MailCharts, Ozark Season 3 is now on Netflix"
            },
            {
              id: 3,
              title: "Water tab mulfunction",
              date: "Wed, Jun. 05, 2019",
              means: "mobile",
              description:
                "MailCharts, come back today to more TV shows & movies."
            },
            {
              id: 4,
              title: "Door handle Broken",
              date: "Mon,     Dec. 14, 2019",
              means: "System",
              description: "MailCharts, Ozark Season 3 is now on Netflix"
            }
          ].map(issue => {
            return (
              <TicketItem
                key={issue.id}
                ticket={issue}
                {...props}
                onClick={handleClickOpen}
              />
            );
          })}
        </Container>
      </Wrapper>
      <SingleTicket
        ticket={ticket}
        open={openTicket}
        handleClose={handleClose}
      />
    </Container>
  );
};

export default Tickets;
