import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import { Avatar, Divider } from "@material-ui/core";
import MailIcon from "@material-ui/icons/Mail";
import BathroomIcon from "@material-ui/icons/BathTub";
import BedIcon from "@material-ui/icons/KingBed";
import GardenIcon from "@material-ui/icons/AmpStories";
import CarIcon from "@material-ui/icons/DirectionsCar";
import GraphIcon from "@material-ui/icons/ShowChart";
import ViewIcon from "@material-ui/icons/Style";
import MiniTicketItem from "./MiniTicketItem";
import MbanzaLogo from "../../../public/static/mbanza.png";
import SingleTicket from "../tickets/SingleTicket";

const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 5px;
`;

const Overview = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };

  const {
    house_type,
    managingAgent,
    house_status,
    appliances,
    ext_ft,
    utilities,
  } = house;
  return (
    <Container flex={1} customStyles={{ paddingTop: 30 }}>
      <Container row center flex={1} customStyles={{ width: "100%" }}>
        <Avatar
          style={{
            backgroundColor: "#0078D4",
            width: 35,
            height: 35,
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <ViewIcon style={{ fontSize: 20 }} />
        </Avatar>
        <Container
          column
          flex={1}
          customStyles={{ width: "100%", paddingLeft: 15, paddingTop: 3 }}
        >
          <SubHeader customStyles={{ fontSize: 16, marginBottom: 0 }}>
            At a glance
          </SubHeader>
          <CaptionText>
            Quick summary about house Barcelona to help you with your email
            reporting.
          </CaptionText>
        </Container>
      </Container>

      <Container
        column
        flex={1}
        customStyles={{
          borderRadius: 3,
          padding: 15,
          marginTop: 10,
          marginBottom: 20,
        }}
        color="white"
      >
        <Container row flex={1} color="transparent">
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            space="space-between"
            color="transparent"
          >
            <Container row center space="space-between" color="transparent">
              <Text
                customStyles={{
                  fontSize: 14,
                  fontWeight: 600,
                  marginBottom: 10,
                }}
              >
                CURRENT TENANT
              </Text>
              {house_status.map((status, i) => (
                <Tag key={i}>
                  {" "}
                  <SubText customStyles={{ fontSize: 13 }}>{status}</SubText>
                </Tag>
              ))}
            </Container>

            <Container row flex={1} center color="transparent">
              <Avatar
                style={{
                  border: "1px dashed #0078D4",
                  backgroundColor: "transparent",
                  width: 30,
                  height: 30,
                  borderRadius: 3,
                  marginRight: 20,
                }}
              />
              <SubHeader customStyles={{ color: "#0078D4", fontSize: 16 }}>
                Rene La
              </SubHeader>
            </Container>
          </Container>
          <Container
            flex={1}
            column
            customStyles={{ padding: 15 }}
            space="space-between"
            color="transparent"
          >
            <Text
              customStyles={{ fontSize: 14, fontWeight: 600, marginBottom: 10 }}
            >
              MANAGEMENT AGENT
            </Text>
            <Container row flex={1} center color="transparent">
              <Avatar
