import React from "react";
import styled from "styled-components";
import {
  Container,
  SubHeader,
  SubText,
  Text,
  CaptionText,
} from "../../styledComponents";
import loadingImg from "../../../public/static/126.gif";
import { Avatar, Divider } from "@material-ui/core";
import ListIcon from "@material-ui/icons/List";
import TicketItem from "../TicketItem";
import SingleTicket from "../tickets/SingleTicket";
import HouseTenantsQuery from "../../queryComponents/landlord/HouseTenants";
import TenantCard from "../TenantCard";

const Wrapper = styled(Container)`
  display: flex;
  flex-direction: column;
  flex: 1;
  padding-top: 30px;
  max-height: calc(100vh - 115px);
  overflow: scroll;
  ::-webkit-scrollbar {
    display: none;
  }
`;
const Tag = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 20px;
  background-color: #e1e1e1;
  padding: 4px 15px;
  margin: 0px 5px;
`;

const Tenants = ({ house, ...other }) => {
  const [ticket, setTicket] = React.useState({});
  const [openTicket, setOpenTicket] = React.useState(false);
  const handleClickOpen = (ticket) => {
    setTicket(ticket);
    setOpenTicket(true);
  };
  const handleClose = () => {
    setOpenTicket(false);
    setTicket({});
  };
  return (
    <HouseTenantsQuery id={house.id}>
      {({ data, loading, error }) => {
        if (loading) {
          return (
            <Container
              flex={1}
              center
              middle
              customStyles={{ paddingTop: 30, minHeight: 300 }}
            >
              <img src={loadingImg} style={{ width: 20, height: 20 }} />
            </Container>
          );
        }
        if (data) {
          const tenants = data.getHouseTenants;

          return (
            <React.Fragment>
              {tenants.length <= 0 ? (
                <Container
                  flex={1}
                  column
                  color="transparent"
                  center
                  middle
                  customStyles={{ minHeight: 200 }}
                >
                  <SubHeader customStyles={{ color: "#979a9a" }}>
                    House has no tenants yet
                  </SubHeader>
                </Container>
              ) : (
                <Container flex={1} customStyles={{ paddingTop: 30 }}>
                  <Container
                    flex={1}
                    row
                    customStyles={{
                      flexWrap: "wrap",
                      justifyContent: "space-between",
                    }}
                  >
                    {tenants.map((tenant) => {
                      return (
                        <TenantCard
                          key={tenant.id}
                          tenant={tenant}
                          {...other}
                        />
                      );
                    })}
                  </Container>
                </Container>
              )}
            </React.Fragment>
          );
        }
        return null;
      }}
    </HouseTenantsQuery>
  );
};

export default Tenants;
