import React from "react";
import clsx from "clsx";
import styled from "styled-components";
import { Container as Wrapper } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import CssBaseline from "@material-ui/core/CssBaseline";
import { theme as customTheme } from "../Page";
import CustomLink from "../CustomLink";
import { Container, SubText, SubHeader, Text } from "../styledComponents";
import hasLogo from "../../public/static/HAS.png";
import { Avatar } from "@material-ui/core";
import ActiveLink from "../ActiveLink";

const useStyles = makeStyles(theme => ({
  menuButton: {
    marginRight: 36
  },
  hide: {
    display: "none"
  },

  drawerPaper: {
    paddingLeft: 100,
    paddingRight: 100,
    height: 64
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    flexDirection: "row",
    justifyContent: "space-between",
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar
  },
  content: {
    backgroundColor: customTheme.bgPrimary
  },
  small: {
    width: theme.spacing(7),
    height: theme.spacing(7)
  }
}));

const WithLayout = Page => props => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(true);

  const handleDrawerOpen = () => {
    setOpen(!open);
  };

  return (
    <Container flex={1} customStyles={{ minHeight: "100vh" }} column>
      <CssBaseline />
      <Drawer
        variant="permanent"
        anchor="top"
        classes={{
          paper: classes.drawerPaper
        }}
      >
        <Container
          // middle
          center
          row
          flex={1}
          color="transparent"
          customStyles={{ height: 64 }}
        >
          <Avatar src={hasLogo} className={classes.small} />
          <Container row flex={1} center>
            <ActiveLink label="Dashboard" href={"/landlord/dashboard"} />
          </Container>
        </Container>
      </Drawer>
      <Container
        flex={1}
        column
        customStyles={{
          paddingTop: 64
        }}
      >
        <Page />
      </Container>
    </Container>
  );
};

export default WithLayout;
