import React from "react";
import styled from "styled-components";
import {
  Container,
  ActionButton,
  Text,
  PrimaryButton
} from "../../../styledComponents";
import UploadIcon from "@material-ui/icons/CloudUpload";
import { withStyles, makeStyles, useTheme } from "@material-ui/core/styles";
import {
  Button,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Avatar
} from "@material-ui/core";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import CloseIcon from "@material-ui/icons/Close";
import AddIcon from "@material-ui/icons/AddCircleOutline";

const useStyles = makeStyles(theme => ({
  dialogPaper: {
    height: 700,
    width: 525,
    borderRadius: 2
  },
  dialogActionStyle: {
    padding: 0,
    margin: 0,
    border: "none"
  },
  dialogContentStyle: {
    display: "flex",
    padding: 0,
    border: "none"
  },
  avatarStyle: {
    width: theme.spacing(8),
    height: theme.spacing(8),
    borderRadius: 2,
    backgroundColor: "#F3F2F1"
  }
}));

const ActionBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 10px;
  padding-left: 10px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.lightGrey};
`;
const CloseBtn = styled(ActionButton)`
  display: flex;
  justify-content: center;
  align-items: center;
  align-content: center;
  padding-right: 6px;
  padding-left: 6px;
  border-radius: 2px;
  background-color: ${({ theme }) => theme.bgPrimary};
`;

const CancelBtn = styled(PrimaryButton)`
  border: none;
  color: ${({ theme }) => theme.primaryDark};
  background-color: transparent;
  &:hover {
    background-color: ${({ theme }) => theme.lightGrey};
  }
`;
const CreateHouse = () => {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [scroll, setScroll] = React.useState("paper");
  const fullScreen = useMediaQuery(theme.breakpoints.down("sm"));

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <ActionBtn onClick={handleClickOpen}>
        <AddIcon style={{ fontSize: 23, marginRight: 5 }} />
        Add New House
      </ActionBtn>
      <Dialog
        fullScreen={fullScreen}
        onClose={handleClose}
        classes={{
          paper: classes.dialogPaper
        }}
        aria-labelledby="customized-dialog-title"
        open={open}
        scroll={scroll}
      >
        <DialogTitle
          id="scroll-dialog-title"
          classes={{
            root: classes.dialogActionStyle
          }}
        >
          <Container
            row
            center
            color="transparent"
            space="space-between"
            customStyles={{
              paddingTop: 15,
              paddingBottom: 7,
              paddingRight: 10,
              paddingLeft: 15
            }}
          >
            <Text>Create New House</Text>
            {/*<CloseBtn onClick={handleClickOpen}>
              <CloseIcon style={{ fontSize: 23 }} />
          </CloseBtn>*/}
          </Container>
        </DialogTitle>
        <DialogContent
          dividers={scroll === "paper"}
          classes={{
            root: classes.dialogContentStyle
          }}
        >
          <Container
            flex={1}
            column
            color="transparent"
            customStyles={{ padding: 10 }}
          >
            <Container color="transparent" column middle center>
              <Avatar className={classes.avatarStyle}>
                <UploadIcon style={{ fontSize: 18, color: "#96b2dd" }} />
              </Avatar>
            </Container>
          </Container>
        </DialogContent>
        <DialogActions
          classes={{
            root: classes.dialogActionStyle
          }}
        >
          <Container
            flex={1}
            row
            center
            space="space-between"
            customStyles={{ paddingLeft: 10 }}
          >
            <CancelBtn onClick={handleClose}>Cancel</CancelBtn>
            <PrimaryButton onClick={handleClose}>Add House</PrimaryButton>
          </Container>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default CreateHouse;
