class CreateParentHouse extends React.Component {
  state = {
    building_name: "",
    address: "",
    image: null,
    showForm: false
  };
  createForm = () => {
    this.setState({ showForm: true });
  };
  closeCreateForm = () => {
    this.setState({ showForm: false });
  };
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  uploadImage = async e => {
    const files = e.target.files;
    const data = new FormData();
    data.append("file", files[0]);
    data.append("upload_preset", "house_assist");

    const res = await fetch(
      "https://api.cloudinary.com/v1_1/dsubmyluz/image/upload/",
      {
        method: "POST",
        body: data
      }
    );
    const file = await res.json();

    if (file) {
      this.setState({
        image: file.secure_url
      });
    }
  };

  CreateFormWrapper = () => {
    const { building_name, address, image, showForm } = this.state;
    const { classes } = this.props;
    return (
      <Fade in={showForm}>
        <Container
          flex={1}
          column
          customStyles={{ zIndex: 1305, paddingTop: 64 }}
        >
          <Container
            row
            center
            space="space-between"
            customStyles={{
              position: "absolute",
              top: 0,
              width: "100%",
              paddingLeft: 10,
              paddingRight: 15,
              height: 64,
              border: `0.5px solid ${fade("#393939", 0.2)}`
            }}
          >
            <div />
            <Typography
              variant="button"
              style={{ fontSize: 13, fontFamily: 400 }}
            >
              Create Building
            </Typography>
            <span
              onClick={this.closeCreateForm}
              style={{ fontSize: 30, fontWeight: 200, cursor: "pointer" }}
            >
              &times;
            </span>
          </Container>
          <Container flex={1} column>
            <Mutation
              mutation={CREATE_PARENT_HOUSE_MUTATION}
              refetchQueries={[{ query: GET_USER_PARENT_HOUSES }]}
              variables={this.state}
            >
              {(createParentHouse, { loading, error }) => {
                return (
                  <Container flex={1} color="inherit" role="presentation">
                    <form
                      method="post"
                      style={{
                        display: "flex",
                        flexDirection: "column",
                        // justifyContent: "center",
                        // alignItems: "center",
                        flex: 1
                      }}
                      onSubmit={async e => {
                        e.preventDefault();

                        await createParentHouse().then(res => {
                          this.closeCreateForm();
                        });
                        this.setState({
                          building_name: "",
                          address: "",
                          image: null
                          // showForm: false
                        });
                      }}
                    >
                      {error &&
                        error.graphQLErrors.map(({ message }, i) => (
                          <p key={i} style={{ color: "red" }}>
                            {message}
                          </p>
                        ))}
                      <Container column center>
                        <Input
                          type="text"
                          name="building"
                          label="building name"
                          value={building_name}
                          style={{
                            width: "50%",
                            marginTop: 40,
                            marginBottom: 20
                          }}
                          onChange={e => {
                            this.setState({ building_name: e.target.value });
                          }}
                        />
                        <Container row>
                          <Input
                            accept="image/*"
                            id="text-button-file"
                            multiple
                            type="file"
                            // value={image}
                            onChange={this.uploadImage}
                          />
                          {image !== null && (
                            <img
                              src={image}
                              style={{
                                width: 40,
                                height: 35,
                                objectFit: "fit"
                              }}
                            />
                          )}
                        </Container>
                      </Container>

                      <Container
                        right
                        center
                        row
                        flex={1}
                        space="space-between"
                        customStyles={{
                          position: "absolute",
                          bottom: 0,
                          marginTop: 15,
                          padding: 10,
                          borderTop: `0.5px solid ${fade("#393939", 0.2)}`,
                          width: "100%"
                        }}
                      >
                        <FlatButton type="submit">
                          {loading ? (
                            <img
                              src={loadingImg}
                              style={{ width: 20, height: 20 }}
                            />
                          ) : (
                            "Save"
                          )}
                        </FlatButton>
                      </Container>
                    </form>
                  </Container>
                );
              }}
            </Mutation>
          </Container>
        </Container>
      </Fade>
    );
  };
  render() {
    const { classes } = this.props;
    const { showForm } = this.state;
    return (
      <Container flex={1}>
        {!showForm && (
          <ParentHouses>
            {({ data, loading }) => {
              if (data) {
                const buildings = data.getUserParentHouses;
                if (buildings.length !== 0) {
                  return (
                    <BuildingsList
                      buildings={buildings}
                      create={this.createForm}
                      showForm={showForm}
                    />
                  );
                }
                if (buildings.length === 0) {
                  return (
                    <Container flex={1}>
                      <Container
                        flex={1}
                        column
                        middle
                        center
                        customStyles={{ paddingTop: 64 }}
                      >
                        <BuildingIcon style={{ fontSize: 60, color: "gray" }} />
                        <Typography
                          variant="button"
                          style={{
                            fontSize: 18,
                            color: "black",
                            fontWeight: 600,
                            marginTop: 15
                          }}
                        >
                          Add New Building
                        </Typography>
                        <Typography
                          style={{
                            fontSize: 13,
                            fontWeight: 200,
                            marginTop: 10
                          }}
                        >
                          You have more than one house with in a
                        </Typography>
                        <Typography
                          style={{
                            fontSize: 13,
                            fontWeight: 200,
                            marginBottom: 15
                          }}
                        >
                          single building, add them in to a building
                        </Typography>
                        <FlatButton onClick={this.createForm}>
                          Add Building
                        </FlatButton>
                      </Container>
                    </Container>
                  );
                }
              }

              if (loading) {
                return (
                  <Container flex={1} middle center>
                    <img src={loadingImg} style={{ width: 25, height: 25 }} />
                  </Container>
                );
              }
              return null;
            }}
          </ParentHouses>
        )}
        {this.CreateFormWrapper()}
      </Container>
    );
  }
}

